import React, { useState, useEffect, useRef } from 'react';
import { 
  Send, 
  Bot, 
  User, 
  Lightbulb, 
  TrendingUp, 
  Target, 
  AlertCircle,
  Sparkles,
  MessageCircle,
  Trash2,
  RotateCcw
} from 'lucide-react';
import { aiFinancialService } from '@/services/aiFinancialService';
import { familyService } from '@/services/familyService';
import { accountService } from '@/services/accountService';
import { transactionService } from '@/services/transactionService';
import { cn } from '@/lib/utils';

interface FinancialAIChatProps {
  familyId: string;
  className?: string;
  compact?: boolean;
}

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  suggestions?: string[];
}

interface FinancialContext {
  totalBalance: number;
  monthlySpending: number;
  savingsGoals: number;
  creditScore: number;
  investments: number;
  recentTransactions: Array<{
    id: string;
    amount: number;
    description: string;
    category: string;
  }>;
}

const FinancialAIChat = ({ familyId, className, compact = false }: FinancialAIChatProps) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [currentInput, setCurrentInput] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [financialContext, setFinancialContext] = useState<FinancialContext | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    loadChatHistory();
    loadFinancialContext();
  }, [familyId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadChatHistory = async () => {
    try {
      const history = aiFinancialService.getChatHistory(familyId);
      setMessages(history);
    } catch (error) {
      console.error('Failed to load chat history:', error);
    }
  };

  const loadFinancialContext = async () => {
    try {
      const [family, accounts, stats, analytics] = await Promise.all([
        familyService.getFamilyById(familyId),
        accountService.getFamilyAccounts(familyId),
        familyService.calculateFamilyStats(familyId),
        transactionService.generateAnalytics(familyId, 'month')
      ]);

      const recentTransactions = await transactionService.searchTransactions(familyId, {
        dateRange: {
          start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
          end: new Date()
        }
      });

      setFinancialContext({
        family,
        accounts,
        recentTransactions: recentTransactions.slice(0, 20),
        budgets: [], // TODO: Add budgets
        stats,
        analytics
      });
    } catch (error) {
      console.error('Failed to load financial context:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!currentInput.trim() || isLoading || !financialContext) return;

    const userMessage: ChatMessage = {
      id: `msg_${Date.now()}_user`,
      type: 'user',
      content: currentInput.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setCurrentInput('');
    setIsLoading(true);

    try {
      const response = await aiFinancialService.processFinancialQuery(
        familyId,
        currentInput.trim(),
        financialContext
      );

      const assistantMessage: ChatMessage = {
        id: `msg_${Date.now()}_assistant`,
        type: 'assistant',
        content: response.content,
        timestamp: new Date(),
        suggestions: response.recommendations
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      const errorMessage: ChatMessage = {
        id: `msg_${Date.now()}_error`,
        type: 'assistant',
        content: 'I apologize, but I encountered an error processing your request. Please try again.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const clearChatHistory = () => {
    aiFinancialService.clearChatHistory(familyId);
    setMessages([]);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatTimestamp = (timestamp: Date) => {
    return timestamp.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const getInsightIcon = (type: string) => {
    switch (type) {
      case 'spending_pattern':
        return <TrendingUp className="w-4 h-4" />;
      case 'savings_opportunity':
        return <Target className="w-4 h-4" />;
      case 'budget_analysis':
        return <AlertCircle className="w-4 h-4" />;
      case 'investment_advice':
        return <Sparkles className="w-4 h-4" />;
      default:
        return <Lightbulb className="w-4 h-4" />;
    }
  };

  const getInsightColor = (impact: string) => {
    switch (impact) {
      case 'high':
        return 'text-red-400 bg-red-500/20 border-red-500/30';
      case 'medium':
        return 'text-yellow-400 bg-yellow-500/20 border-yellow-500/30';
      case 'low':
        return 'text-green-400 bg-green-500/20 border-green-500/30';
      default:
        return 'text-blue-400 bg-blue-500/20 border-blue-500/30';
    }
  };

  const suggestedQuestions = [
    "How can I improve my savings rate?",
    "What should I focus on in my budget?",
    "Is my spending healthy this month?",
    "Should I invest more or pay down debt?",
    "How's my emergency fund looking?"
  ];

  if (!financialContext) {
    return (
      <div className={cn("flex items-center justify-center p-8", className)}>
        <div className="text-center">
          <Bot className="w-12 h-12 text-blue-400 mx-auto mb-4 animate-pulse" />
          <p className="text-white/60">Loading your financial context...</p>
        </div>
      </div>
    );
  }

  return (
    <div className={cn("flex flex-col h-full bg-white/[0.02] rounded-2xl border border-white/[0.08]", className)}>
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-white/[0.08]">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-blue-500/20 rounded-xl flex items-center justify-center">
            <Bot className="w-6 h-6 text-blue-400" />
          </div>
          <div>
            <h3 className="font-semibold text-white">AI Financial Advisor</h3>
            <p className="text-white/60 text-sm">
              {compact ? 'Ask me anything' : 'Personalized advice based on your financial data'}
            </p>
          </div>
        </div>

        {messages.length > 0 && (
          <div className="flex items-center gap-2">
            <button
              onClick={clearChatHistory}
              className="p-2 rounded-lg hover:bg-white/[0.05] transition-colors text-white/60 hover:text-white"
              title="Clear chat"
            >
              <Trash2 className="w-4 h-4" />
            </button>
          </div>
        )}
      </div>

      {/* Messages */}
      <div className={cn("flex-1 overflow-y-auto p-4 space-y-4", compact ? "max-h-96" : "min-h-[400px]")}>
        {messages.length === 0 ? (
          <div className="text-center py-8">
            <Bot className="w-16 h-16 text-blue-400/50 mx-auto mb-4" />
            <h4 className="text-white font-medium mb-2">Hi there! I'm your AI financial advisor.</h4>
            <p className="text-white/60 text-sm mb-6">
              I have access to your complete financial picture and can provide personalized advice.
            </p>

            <div className="space-y-2">
              <p className="text-white/80 text-sm font-medium">Try asking:</p>
              <div className="space-y-2">
                {suggestedQuestions.slice(0, compact ? 3 : 5).map((question, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentInput(question)}
                    className="block w-full text-left p-3 bg-white/[0.03] hover:bg-white/[0.05] border border-white/[0.05] rounded-lg text-white/80 hover:text-white text-sm transition-all"
                  >
                    "{question}"
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div key={message.id} className={cn("flex gap-3", message.type === 'user' ? 'justify-end' : 'justify-start')}>
              <div className={cn("flex gap-3 max-w-[80%]", message.type === 'user' ? 'flex-row-reverse' : 'flex-row')}>
                {/* Avatar */}
                <div className={cn(
                  "w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0",
                  message.type === 'user' 
                    ? "bg-green-500/20" 
                    : "bg-blue-500/20"
                )}>
                  {message.type === 'user' ? (
                    <User className="w-4 h-4 text-green-400" />
                  ) : (
                    <Bot className="w-4 h-4 text-blue-400" />
                  )}
                </div>

                {/* Message Content */}
                <div className={cn(
                  "flex flex-col gap-2",
                  message.type === 'user' ? 'items-end' : 'items-start'
                )}>
                  <div className={cn(
                    "rounded-2xl px-4 py-3 max-w-full",
                    message.type === 'user'
                      ? "bg-blue-500 text-white"
                      : "bg-white/[0.05] border border-white/[0.08] text-white"
                  )}>
                    <p className="text-sm leading-relaxed whitespace-pre-wrap">{message.content}</p>
                  </div>

                  {/* Suggestions */}
                  {message.suggestions && message.suggestions.length > 0 && (
                    <div className="space-y-2 w-full max-w-lg">
                      {message.suggestions.map((suggestion, index) => (
                        <div
                          key={index}
                          className="p-3 bg-purple-500/20 border border-purple-500/30 rounded-xl text-sm"
                        >
                          <div className="flex items-center gap-2 mb-2">
                            <Target className="w-4 h-4 text-purple-400" />
                            <span className="font-medium text-purple-400">{suggestion}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  <span className="text-xs text-white/40">{formatTimestamp(message.timestamp)}</span>
                </div>
              </div>
            </div>
          ))
        )}

        {isLoading && (
          <div className="flex gap-3 justify-start">
            <div className="w-8 h-8 bg-blue-500/20 rounded-full flex items-center justify-center">
              <Bot className="w-4 h-4 text-blue-400" />
            </div>
            <div className="bg-white/[0.05] border border-white/[0.08] rounded-2xl px-4 py-3">
              <div className="flex items-center gap-2">
                <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce" />
                <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }} />
                <div className="w-2 h-2 bg-blue-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-4 border-t border-white/[0.08]">
        <div className="flex gap-3">
          <textarea
            ref={inputRef}
            value={currentInput}
            onChange={(e) => setCurrentInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask me about your finances..."
            className="flex-1 bg-white/[0.05] border border-white/[0.08] rounded-xl px-4 py-3 text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50 resize-none"
            rows={1}
            disabled={isLoading}
          />
          <button
            onClick={handleSendMessage}
            disabled={!currentInput.trim() || isLoading}
            className={cn(
              "p-3 rounded-xl transition-all",
              currentInput.trim() && !isLoading
                ? "bg-blue-500 hover:bg-blue-600 text-white"
                : "bg-white/[0.05] text-white/40 cursor-not-allowed"
            )}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default FinancialAIChat;

import React, { useState, useEffect } from 'react';
import {
  PieChart,
  BarChart3,
  TrendingUp,
  TrendingDown,
  Target,
  AlertCircle,
  Plus,
  Settings,
  Calendar,
  DollarSign,
  CheckCircle,
  Clock,
  ArrowRight
} from 'lucide-react';
import { budgetService } from '@/services/budgetService';
import { Budget, BudgetCategory, SavingsGoal } from '@/types/budgets';
import { cn } from '@/lib/utils';

interface BudgetTrackerProps {
  familyId: string;
  className?: string;
}

const BudgetTracker = ({ familyId, className }: BudgetTrackerProps) => {
  const [budget, setBudget] = useState<Budget | null>(null);
  const [goals, setGoals] = useState<SavingsGoal[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedView, setSelectedView] = useState<'overview' | 'categories' | 'goals' | 'analytics'>('overview');

  useEffect(() => {
    loadBudgetData();
  }, [familyId, loadBudgetData]);

  const loadBudgetData = async () => {
    try {
      setLoading(true);
      const [activeBudget, familyGoals] = await Promise.all([
        budgetService.getActiveBudget(familyId),
        budgetService.getFamilySavingsGoals(familyId)
      ]);

      setBudget(activeBudget);
      setGoals(familyGoals);
    } catch (error) {
      console.error('Failed to load budget data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${value.toFixed(1)}%`;
  };

  const getCategoryProgress = (category: BudgetCategory) => {
    if (category.budgetedAmount === 0) return 0;
    return Math.min((category.spentAmount / category.budgetedAmount) * 100, 100);
  };

  const getCategoryStatus = (category: BudgetCategory) => {
    const progress = getCategoryProgress(category);
    if (progress >= 100) return 'overspent';
    if (progress >= 80) return 'warning';
    if (progress >= 50) return 'on-track';
    return 'under-budget';
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'overspent':
        return 'text-red-400 bg-red-500/20 border-red-500/30';
      case 'warning':
        return 'text-yellow-400 bg-yellow-500/20 border-yellow-500/30';
      case 'on-track':
        return 'text-blue-400 bg-blue-500/20 border-blue-500/30';
      case 'under-budget':
        return 'text-green-400 bg-green-500/20 border-green-500/30';
      default:
        return 'text-gray-400 bg-gray-500/20 border-gray-500/30';
    }
  };

  const getGoalProgress = (goal: SavingsGoal) => {
    return (goal.currentAmount / goal.targetAmount) * 100;
  };

  const getGoalStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="w-4 h-4 text-green-400" />;
      case 'on_track':
      case 'ahead':
        return <TrendingUp className="w-4 h-4 text-green-400" />;
      case 'behind':
        return <TrendingDown className="w-4 h-4 text-red-400" />;
      default:
        return <Clock className="w-4 h-4 text-yellow-400" />;
    }
  };

  if (loading) {
    return (
      <div className={cn("space-y-6", className)}>
        {[...Array(3)].map((_, i) => (
          <div key={i} className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 animate-pulse">
            <div className="flex items-center justify-between mb-4">
              <div className="h-6 bg-white/[0.05] rounded w-32"></div>
              <div className="h-6 bg-white/[0.05] rounded w-24"></div>
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-white/[0.05] rounded w-full"></div>
              <div className="h-4 bg-white/[0.05] rounded w-3/4"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  const renderOverview = () => (
    <div className="space-y-6">
      {/* Budget Summary */}
      {budget && (
        <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
          <h3 className="text-xl font-bold text-white mb-6 flex items-center gap-3">
            <PieChart className="w-6 h-6 text-blue-400" />
            {budget.name}
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="text-center">
              <p className="text-white/60 text-sm mb-2">Total Budget</p>
              <p className="text-2xl font-bold text-white">{formatCurrency(budget.totalBudgeted)}</p>
            </div>

            <div className="text-center">
              <p className="text-white/60 text-sm mb-2">Spent</p>
              <p className="text-2xl font-bold text-red-400">{formatCurrency(budget.totalSpent)}</p>
            </div>

            <div className="text-center">
              <p className="text-white/60 text-sm mb-2">Remaining</p>
              <p className="text-2xl font-bold text-green-400">{formatCurrency(budget.totalRemaining)}</p>
            </div>

            <div className="text-center">
              <p className="text-white/60 text-sm mb-2">Savings Rate</p>
              <p className="text-2xl font-bold text-blue-400">
                {formatPercentage((budget.totalRemaining / budget.totalBudgeted) * 100)}
              </p>
            </div>
          </div>

          {/* Overall Progress Bar */}
          <div className="mt-6">
            <div className="flex justify-between text-sm text-white/60 mb-2">
              <span>Budget Progress</span>
              <span>{formatPercentage((budget.totalSpent / budget.totalBudgeted) * 100)}</span>
            </div>
            <div className="w-full bg-white/[0.05] rounded-full h-3">
              <div 
                className={cn(
                  "h-3 rounded-full transition-all duration-300",
                  budget.totalSpent > budget.totalBudgeted ? "bg-red-500" : "bg-blue-500"
                )}
                style={{ width: `${Math.min((budget.totalSpent / budget.totalBudgeted) * 100, 100)}%` }}
              ></div>
            </div>
          </div>
        </div>
      )}

      {/* Top Categories */}
      {budget && budget.categories.length > 0 && (
        <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-3">
            <BarChart3 className="w-5 h-5 text-blue-400" />
            Top Spending Categories
          </h3>

          <div className="space-y-4">
            {budget.categories
              .sort((a, b) => b.spentAmount - a.spentAmount)
              .slice(0, 5)
              .map((category) => {
                const progress = getCategoryProgress(category);
                const status = getCategoryStatus(category);

                return (
                  <div key={category.id} className="flex items-center justify-between">
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      <div className="w-8 h-8 rounded-lg bg-blue-500/20 flex items-center justify-center">
                        <Target className="w-4 h-4 text-blue-400" />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-white capitalize truncate">{category.categoryName.replace('_', ' ')}</p>
                        <div className="flex items-center gap-3 mt-1">
                          <div className="flex-1 bg-white/[0.05] rounded-full h-2">
                            <div 
                              className={cn(
                                "h-2 rounded-full transition-all duration-300",
                                progress >= 100 ? "bg-red-500" : 
                                progress >= 80 ? "bg-yellow-500" : "bg-green-500"
                              )}
                              style={{ width: `${Math.min(progress, 100)}%` }}
                            ></div>
                          </div>
                          <span className={cn(
                            "text-xs px-2 py-1 rounded-lg border font-medium",
                            getStatusColor(status)
                          )}>
                            {formatPercentage(progress)}
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="text-right">
                      <p className="font-bold text-white">{formatCurrency(category.spentAmount)}</p>
                      <p className="text-white/60 text-sm">of {formatCurrency(category.budgetedAmount)}</p>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      )}

      {/* Savings Goals */}
      {goals.length > 0 && (
        <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-3">
            <Target className="w-5 h-5 text-green-400" />
            Savings Goals
          </h3>

          <div className="space-y-4">
            {goals.slice(0, 3).map((goal) => {
              const progress = getGoalProgress(goal);

              return (
                <div key={goal.id} className="flex items-center justify-between">
                  <div className="flex items-center gap-3 flex-1 min-w-0">
                    <div className="w-8 h-8 rounded-lg bg-green-500/20 flex items-center justify-center">
                      {getGoalStatusIcon(goal.status)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-white truncate">{goal.name}</p>
                      <div className="flex items-center gap-3 mt-1">
                        <div className="flex-1 bg-white/[0.05] rounded-full h-2">
                          <div 
                            className="h-2 rounded-full bg-green-500 transition-all duration-300"
                            style={{ width: `${Math.min(progress, 100)}%` }}
                          ></div>
                        </div>
                        <span className="text-xs text-green-400 font-medium">
                          {formatPercentage(progress)}
                        </span>
                      </div>
                    </div>
                  </div>

                  <div className="text-right">
                    <p className="font-bold text-white">{formatCurrency(goal.currentAmount)}</p>
                    <p className="text-white/60 text-sm">of {formatCurrency(goal.targetAmount)}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );

  const renderCategories = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-bold text-white">Budget Categories</h3>
        <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-xl transition-colors flex items-center gap-2">
          <Plus className="w-4 h-4" />
          Add Category
        </button>
      </div>

      {budget && budget.categories.map((category) => {
        const progress = getCategoryProgress(category);
        const status = getCategoryStatus(category);

        return (
          <div
            key={category.id}
            className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 hover:bg-white/[0.03] transition-all duration-300"
          >
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 rounded-2xl bg-blue-500/20 flex items-center justify-center">
                  <Target className="w-6 h-6 text-blue-400" />
                </div>
                <div>
                  <h4 className="font-semibold text-white capitalize">{category.categoryName.replace('_', ' ')}</h4>
                  <div className="flex items-center gap-3 mt-1">
                    {category.isEssential && (
                      <span className="text-xs px-2 py-1 rounded-lg bg-red-500/20 text-red-400 border border-red-500/30">
                        Essential
                      </span>
                    )}
                    {category.isFixed && (
                      <span className="text-xs px-2 py-1 rounded-lg bg-blue-500/20 text-blue-400 border border-blue-500/30">
                        Fixed
                      </span>
                    )}
                  </div>
                </div>
              </div>

              <div className="text-right">
                <p className="font-bold text-white">{formatCurrency(category.spentAmount)}</p>
                <p className="text-white/60 text-sm">of {formatCurrency(category.budgetedAmount)}</p>
                <span className={cn(
                  "text-xs px-2 py-1 rounded-lg border font-medium inline-block mt-1",
                  getStatusColor(status)
                )}>
                  {formatPercentage(progress)}
                </span>
              </div>
            </div>

            {/* Progress Bar */}
            <div className="mb-4">
              <div className="flex justify-between text-sm text-white/60 mb-2">
                <span>Spent</span>
                <span>Remaining: {formatCurrency(category.remainingAmount)}</span>
              </div>
              <div className="w-full bg-white/[0.05] rounded-full h-3">
                <div 
                  className={cn(
                    "h-3 rounded-full transition-all duration-300",
                    progress >= 100 ? "bg-red-500" : 
                    progress >= 80 ? "bg-yellow-500" : "bg-green-500"
                  )}
                  style={{ width: `${Math.min(progress, 100)}%` }}
                ></div>
              </div>
            </div>

            {/* Overage Warning */}
            {category.overageAmount > 0 && (
              <div className="bg-red-500/10 border border-red-500/20 rounded-xl p-3 flex items-center gap-3">
                <AlertCircle className="w-5 h-5 text-red-400" />
                <div>
                  <p className="text-red-400 font-medium">Over Budget</p>
                  <p className="text-red-300 text-sm">Exceeded by {formatCurrency(category.overageAmount)}</p>
                </div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );

  const renderGoals = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-bold text-white">Savings Goals</h3>
        <button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-xl transition-colors flex items-center gap-2">
          <Plus className="w-4 h-4" />
          Add Goal
        </button>
      </div>

      {goals.map((goal) => {
        const progress = getGoalProgress(goal);
        const monthsRemaining = Math.ceil((goal.targetDate.getTime() - Date.now()) / (30 * 24 * 60 * 60 * 1000));

        return (
          <div
            key={goal.id}
            className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 hover:bg-white/[0.03] transition-all duration-300"
          >
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 rounded-2xl bg-green-500/20 flex items-center justify-center">
                  {getGoalStatusIcon(goal.status)}
                </div>
                <div>
                  <h4 className="font-semibold text-white">{goal.name}</h4>
                  <p className="text-white/60 text-sm">{goal.description}</p>
                  <div className="flex items-center gap-3 mt-1">
                    <span className="text-xs px-2 py-1 rounded-lg bg-green-500/20 text-green-400 border border-green-500/30 capitalize">
                      {goal.goalType.replace('_', ' ')}
                    </span>
                    <span className="text-xs text-white/60">
                      <Calendar className="w-3 h-3 inline mr-1" />
                      {monthsRemaining > 0 ? `${monthsRemaining} months left` : 'Overdue'}
                    </span>
                  </div>
                </div>
              </div>

              <div className="text-right">
                <p className="font-bold text-white">{formatCurrency(goal.currentAmount)}</p>
                <p className="text-white/60 text-sm">of {formatCurrency(goal.targetAmount)}</p>
                <p className="text-green-400 text-sm font-medium mt-1">{formatPercentage(progress)}</p>
              </div>
            </div>

            {/* Progress Bar */}
            <div className="mb-4">
              <div className="flex justify-between text-sm text-white/60 mb-2">
                <span>Progress</span>
                <span>Monthly: {formatCurrency(goal.monthlyContribution)}</span>
              </div>
              <div className="w-full bg-white/[0.05] rounded-full h-3">
                <div 
                  className="h-3 rounded-full bg-green-500 transition-all duration-300"
                  style={{ width: `${Math.min(progress, 100)}%` }}
                ></div>
              </div>
            </div>

            {/* Goal Details */}
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center gap-4">
                <span className="text-white/60">Target Date:</span>
                <span className="text-white">{goal.targetDate.toLocaleDateString()}</span>
              </div>
              {goal.autoContribute && (
                <span className="text-green-400 flex items-center gap-1">
                  <CheckCircle className="w-3 h-3" />
                  Auto-contribute
                </span>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );

  return (
    <div className={cn("space-y-6", className)}>
      {/* Navigation Tabs */}
      <div className="flex items-center gap-2 bg-white/[0.02] rounded-xl p-1 border border-white/[0.08]">
        {[
          { id: 'overview', label: 'Overview', icon: PieChart },
          { id: 'categories', label: 'Categories', icon: BarChart3 },
          { id: 'goals', label: 'Goals', icon: Target },
          { id: 'analytics', label: 'Analytics', icon: TrendingUp }
        ].map(({ id, label, icon: Icon }) => (
          <button
            key={id}
            onClick={(e: React.MouseEvent) => e.stopPropagation()}
            className={cn(
              "flex-1 flex items-center justify-center gap-2 px-4 py-2 rounded-lg transition-all",
              selectedView === id
                ? "bg-blue-500 text-white"
                : "text-white/60 hover:text-white hover:bg-white/[0.05]"
            )}
          >
            <Icon className="w-4 h-4" />
            {label}
