      {/* Content */}
      {selectedView === 'overview' && renderOverview()}
      {selectedView === 'categories' && renderCategories()}
      {selectedView === 'goals' && renderGoals()}
      {selectedView === 'analytics' && renderOverview()} {/* Reuse overview for now */}
