export type TransactionVariant = 'default' | 'apple' | 'clean' | 'polished' | 'enterprise' | 'mobile';

export interface VueniTransaction {
  id: string;
  merchant: string;
  category: {
    name: string;
    color: string;
  };
  amount: number;
  date: string;
  status: 'completed' | 'pending' | 'failed';
  scores?: {
    health: number;
    eco: number;
    financial: number;
  };
  description?: string;
  reference?: string;
  tags?: string[];
}

