import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { 
  Search, 
  Filter, 
  Download, 
  Upload, 
  Plus,
  Edit3,
  Trash2,
  ArrowUpDown,
  Calendar,
  DollarSign,
  Tag,
  Settings,
  CheckSquare,
  Square,
  MoreHorizontal,
  TrendingUp,
  ArrowLeftRight,
  AlertCircle
} from 'lucide-react';
import { Transaction, TransactionCategory } from '@/types/transactions';
import { transactionService } from '@/services/transactionService';
import { Switch } from '@/components/ui/switch';
import { cn } from '@/lib/utils';

interface TransactionManagerProps {
  familyId: string;
  accountId?: string;
  compact?: boolean;
}

const TransactionManager = ({ familyId, accountId, compact = false }: TransactionManagerProps) => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [selectedTransactions, setSelectedTransactions] = useState<Set<string>>(new Set());
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategories, setSelectedCategories] = useState<TransactionCategory[]>([]);
  const [dateRange, setDateRange] = useState<{ start: Date; end: Date } | null>(null);
  const [showFilters, setShowFilters] = useState(false);
  const [showBulkActions, setShowBulkActions] = useState(false);
  const [sortBy, setSortBy] = useState<'date' | 'amount' | 'merchant'>('date');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [loading, setLoading] = useState(true);
  const [analytics, setAnalytics] = useState<any>(null);

  // Load transactions on component mount and when filters change
  useEffect(() => {
    loadTransactions();
  }, [familyId, accountId, searchQuery, selectedCategories, dateRange]);

  const loadTransactions = async () => {
    setLoading(true);
    try {
      const filters: any = {
        query: searchQuery || undefined,
        categories: selectedCategories.length > 0 ? selectedCategories : undefined,
        accountIds: accountId ? [accountId] : undefined,
        dateRange: dateRange || undefined,
        excludeTransfers: false
      };

      const [transactionData, analyticsData] = await Promise.all([
        transactionService.searchTransactions(familyId, filters),
        transactionService.generateAnalytics(familyId, 'month')
      ]);

      setTransactions(transactionData);
      setAnalytics(analyticsData);
    } catch (error) {
      console.error('Failed to load transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  const sortedTransactions = useMemo(() => {
    return [...transactions].sort((a, b) => {
      let comparison = 0;

      switch (sortBy) {
        case 'date':
          comparison = a.date.getTime() - b.date.getTime();
          break;
        case 'amount':
          comparison = Math.abs(a.amount) - Math.abs(b.amount);
          break;
        case 'merchant':
          comparison = (a.merchantName || '').localeCompare(b.merchantName || '');
          break;
      }

      return sortOrder === 'desc' ? -comparison : comparison;
    });
  }, [transactions, sortBy, sortOrder]);

  const handleSelectTransaction = useCallback((transactionId: string, selected: boolean) => {
    const newSelected = new Set(selectedTransactions);
    if (selected) {
      newSelected.add(transactionId);
    } else {
      newSelected.delete(transactionId);
    }
    setSelectedTransactions(newSelected);
    setShowBulkActions(newSelected.size > 0);
  }, [selectedTransactions]);

  const handleSelectAll = useCallback(() => {
    if (selectedTransactions.size === transactions.length) {
      setSelectedTransactions(new Set());
      setShowBulkActions(false);
    } else {
      setSelectedTransactions(new Set(transactions.map(t => t.id)));
      setShowBulkActions(true);
    }
  }, [selectedTransactions.size, transactions]);

  const handleBulkUpdate = async (updates: any) => {
    try {
      await transactionService.bulkUpdateTransactions(
        Array.from(selectedTransactions),
        updates
      );
      await loadTransactions();
      setSelectedTransactions(new Set());
      setShowBulkActions(false);
    } catch (error) {
      console.error('Bulk update failed:', error);
    }
  };

  const getTransactionIcon = (transaction: Transaction) => {
    if (transaction.isTransfer) {
      return <ArrowLeftRight className="w-4 h-4 text-blue-400" />;
    }
    if (transaction.amount > 0) {
      return <TrendingUp className="w-4 h-4 text-green-400" />;
    }
    return <DollarSign className="w-4 h-4 text-white/70" />;
  };

  const getCategoryColor = (category: TransactionCategory) => {
    const colors: Record<TransactionCategory, string> = {
      income: 'bg-green-500/20 text-green-400 border-green-500/30',
      housing: 'bg-blue-500/20 text-blue-400 border-blue-500/30',
      transportation: 'bg-orange-500/20 text-orange-400 border-orange-500/30',
      food: 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30',
      utilities: 'bg-purple-500/20 text-purple-400 border-purple-500/30',
      insurance: 'bg-indigo-500/20 text-indigo-400 border-indigo-500/30',
      healthcare: 'bg-red-500/20 text-red-400 border-red-500/30',
      savings: 'bg-emerald-500/20 text-emerald-400 border-emerald-500/30',
      debt_payments: 'bg-rose-500/20 text-rose-400 border-rose-500/30',
      entertainment: 'bg-pink-500/20 text-pink-400 border-pink-500/30',
      personal_care: 'bg-violet-500/20 text-violet-400 border-violet-500/30',
      shopping: 'bg-cyan-500/20 text-cyan-400 border-cyan-500/30',
      education: 'bg-teal-500/20 text-teal-400 border-teal-500/30',
      gifts_donations: 'bg-amber-500/20 text-amber-400 border-amber-500/30',
      business: 'bg-slate-500/20 text-slate-400 border-slate-500/30',
      taxes: 'bg-red-600/20 text-red-300 border-red-600/30',
      investments: 'bg-green-600/20 text-green-300 border-green-600/30',
      fees: 'bg-gray-500/20 text-gray-400 border-gray-500/30',
      transfers: 'bg-blue-500/20 text-blue-400 border-blue-500/30',
      other: 'bg-gray-500/20 text-gray-400 border-gray-500/30'
    };
    return colors[category] || colors.other;
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatDate = (date: Date) => {
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: date.getFullYear() !== new Date().getFullYear() ? 'numeric' : undefined
    });
  };

  if (loading) {
    return (
      <div className="space-y-4">
        {[...Array(5)].map((_, i) => (
          <div key={i} className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-4 animate-pulse">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-white/[0.05] rounded-2xl"></div>
                <div className="space-y-2">
                  <div className="h-4 bg-white/[0.05] rounded w-32"></div>
                  <div className="h-3 bg-white/[0.05] rounded w-24"></div>
                </div>
              </div>
              <div className="h-6 bg-white/[0.05] rounded w-16"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header & Controls */}
      {!compact && (
        <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
          <div className="flex items-center justify-between mb-6">
            <div>
              <h2 className="text-2xl font-bold text-white flex items-center gap-3">
                <DollarSign className="w-7 h-7 text-blue-400" />
                Transactions
              </h2>
              <p className="text-white/60 mt-1">
                {transactions.length} transaction{transactions.length !== 1 ? 's' : ''}
                {analytics && (
                  <span className="ml-2">
                    â€¢ ${Math.abs(analytics.totalExpenses).toLocaleString()} spent this month
                  </span>
                )}
              </p>
            </div>

            <div className="flex items-center gap-3">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className={cn(
                  "liquid-glass-button px-4 py-2 rounded-xl transition-all flex items-center gap-2",
                  showFilters ? "bg-blue-500/20 text-blue-400" : "text-white/80 hover:text-white"
                )}
              >
                <Filter className="w-4 h-4" />
                Filters
              </button>

              <button className="liquid-glass-button px-4 py-2 rounded-xl text-white/80 hover:text-white transition-all flex items-center gap-2">
                <Upload className="w-4 h-4" />
                Import
              </button>

              <button className="liquid-glass-button px-4 py-2 rounded-xl text-white/80 hover:text-white transition-all flex items-center gap-2">
                <Download className="w-4 h-4" />
                Export
              </button>
            </div>
          </div>

          {/* Search Bar */}
          <div className="relative mb-4">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-white/40" />
            <input
              type="text"
              placeholder="Search transactions, merchants, descriptions..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-white/[0.05] border border-white/[0.08] rounded-xl text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50"
            />
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="bg-white/[0.03] rounded-xl p-4 border border-white/[0.05] space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-white/80 mb-2">Categories</label>
                  <select
                    multiple
                    className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm"
                  >
                    {Object.values(['income', 'housing', 'food', 'transportation', 'entertainment']).map(category => (
                      <option key={category} value={category}>
                        {category.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-white/80 mb-2">Date Range</label>
                  <select className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm py-2 px-3">
                    <option value="">All Time</option>
                    <option value="7d">Last 7 Days</option>
                    <option value="30d">Last 30 Days</option>
                    <option value="90d">Last 90 Days</option>
                    <option value="1y">Last Year</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-white/80 mb-2">Amount Range</label>
                  <div className="flex gap-2">
                    <input
                      type="number"
                      placeholder="Min"
                      className="flex-1 bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm py-2 px-3"
                    />
                    <input
                      type="number"
                      placeholder="Max"
                      className="flex-1 bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm py-2 px-3"
                    />
                  </div>
                </div>
              </div>

              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                  <Switch id="exclude-transfers" />
                  <label htmlFor="exclude-transfers" className="text-sm text-white/80">
                    Exclude Transfers
                  </label>
                </div>
                <div className="flex items-center gap-2">
                  <Switch id="pending-only" />
                  <label htmlFor="pending-only" className="text-sm text-white/80">
                    Pending Only
                  </label>
                </div>
              </div>
            </div>
          )}

          {/* Sort Controls */}
          <div className="flex items-center justify-between mt-4">
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <button
                  onClick={handleSelectAll}
                  className="p-2 rounded-lg hover:bg-white/[0.05] transition-colors"
                >
                  {selectedTransactions.size === transactions.length ? (
                    <CheckSquare className="w-4 h-4 text-blue-400" />
                  ) : selectedTransactions.size > 0 ? (
                    <Square className="w-4 h-4 text-blue-400 opacity-60" />
                  ) : (
                    <Square className="w-4 h-4 text-white/60" />
                  )}
                </button>
                {selectedTransactions.size > 0 && (
                  <span className="text-sm text-white/80">
                    {selectedTransactions.size} selected
                  </span>
                )}
              </div>
            </div>

            <div className="flex items-center gap-2">
              <span className="text-sm text-white/60">Sort by:</span>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value as any)}
                className="bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm py-1 px-2"
              >
                <option value="date">Date</option>
                <option value="amount">Amount</option>
                <option value="merchant">Merchant</option>
              </select>
              <button
                onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                className="p-1 rounded hover:bg-white/[0.05] transition-colors"
              >
                <ArrowUpDown className="w-4 h-4 text-white/60" />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Bulk Actions Bar */}
      {showBulkActions && (
        <div className="bg-blue-500/10 border border-blue-500/20 rounded-xl p-4">
          <div className="flex items-center justify-between">
            <span className="text-blue-400 font-medium">
              {selectedTransactions.size} transaction{selectedTransactions.size !== 1 ? 's' : ''} selected
            </span>

            <div className="flex items-center gap-3">
              <select
                onChange={(e) => e.target.value && handleBulkUpdate({ category: e.target.value })}
                className="bg-white/[0.05] border border-white/[0.08] rounded-lg text-white text-sm py-2 px-3"
              >
                <option value="">Set Category...</option>
                {Object.values(['income', 'housing', 'food', 'transportation', 'entertainment']).map(category => (
                  <option key={category} value={category}>
                    {category.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                  </option>
                ))}
              </select>

              <button
                onClick={() => handleBulkUpdate({ excludeFromBudget: true })}
                className="px-3 py-2 bg-white/[0.05] border border-white/[0.08] rounded-lg text-white/80 hover:text-white text-sm transition-colors"
              >
                Exclude from Budget
              </button>

              <button
                onClick={() => setSelectedTransactions(new Set())}
                className="p-2 text-white/60 hover:text-white transition-colors"
              >
                âœ•
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Transaction List */}
      <div className="space-y-3">
        {sortedTransactions.length === 0 ? (
          <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-12 text-center">
            <DollarSign className="w-16 h-16 text-white/20 mx-auto mb-4" />
            <h3 className="text-xl font-bold text-white mb-2">No Transactions Found</h3>
            <p className="text-white/60">
              {searchQuery || selectedCategories.length > 0 
                ? 'Try adjusting your search or filters'
                : 'Your transactions will appear here once you link accounts'
              }
            </p>
          </div>
        ) : (
          sortedTransactions.map((transaction) => (
            <div
              key={transaction.id}
              className={cn(
                "bg-white/[0.02] rounded-2xl border border-white/[0.08] p-4 hover:bg-white/[0.03] transition-all duration-300 backdrop-blur-md",
                selectedTransactions.has(transaction.id) && "ring-2 ring-blue-500/50 bg-blue-500/5"
              )}
            >
              <div className="flex items-center gap-4">
                {/* Selection Checkbox */}
                <button
                  onClick={() => handleSelectTransaction(transaction.id, !selectedTransactions.has(transaction.id))}
                  className="flex-shrink-0"
                >
                  {selectedTransactions.has(transaction.id) ? (
                    <CheckSquare className="w-4 h-4 text-blue-400" />
                  ) : (
                    <Square className="w-4 h-4 text-white/40 hover:text-white/60" />
                  )}
                </button>

                {/* Transaction Icon */}
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 rounded-2xl bg-white/[0.06] flex items-center justify-center">
                    {getTransactionIcon(transaction)}
                  </div>
                </div>

                {/* Transaction Details */}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <div className="min-w-0 flex-1">
                      <h4 className="font-semibold text-white truncate">
                        {transaction.merchantName || transaction.description}
                      </h4>
                      <div className="flex items-center gap-3 mt-1">
                        <span className={cn(
                          "text-xs px-2 py-1 rounded-lg border font-medium",
                          getCategoryColor(transaction.category)
                        )}>
                          {transaction.category.replace('_', ' ')}
                        </span>
                        <span className="text-xs text-white/60">
                          {formatDate(transaction.date)}
                        </span>
                        {transaction.isTransfer && (
                          <span className="text-xs text-blue-400 flex items-center gap-1">
                            <ArrowLeftRight className="w-3 h-3" />
                            Transfer
                          </span>
                        )}
                        {transaction.isPending && (
                          <span className="text-xs text-orange-400 flex items-center gap-1">
                            <AlertCircle className="w-3 h-3" />
                            Pending
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="text-right flex-shrink-0 ml-4">
                      <p className={cn(
                        "font-bold text-sm",
                        transaction.amount > 0 ? "text-green-400" : "text-white"
                      )}>
                        {transaction.amount > 0 ? '+' : ''}{formatCurrency(transaction.amount)}
                      </p>
                      {transaction.tags.length > 0 && (
                        <div className="flex items-center gap-1 mt-1 justify-end">
                          <Tag className="w-3 h-3 text-white/40" />
                          <span className="text-xs text-white/60">
                            {transaction.tags.slice(0, 2).join(', ')}
                            {transaction.tags.length > 2 && '...'}
                          </span>
                        </div>
                      )}
                    </div>

                    {/* Action Menu */}
                    <div className="flex-shrink-0 ml-2">
                      <button className="p-2 rounded-lg hover:bg-white/[0.05] transition-colors">
                        <MoreHorizontal className="w-4 h-4 text-white/60" />
                      </button>
                    </div>
                  </div>

                  {/* Transaction Description */}
                  {transaction.description !== transaction.merchantName && (
                    <p className="text-xs text-white/60 mt-2 truncate">
                      {transaction.description}
                    </p>
                  )}

                  {/* Notes */}
                  {transaction.notes && (
                    <p className="text-xs text-white/50 mt-1 italic">
                      {transaction.notes}
                    </p>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TransactionManager;

import React, { useState, useMemo, useCallback } from 'react';
import { 
  Search, 
  Filter, 
  ChevronDown, 
  TrendingUp, 
  TrendingDown, 
  Minus,
  Calendar,
  Download,
  Eye,
  EyeOff
} from 'lucide-react';
import { UniversalCard } from '../ui/UniversalCard';
import { UniversalScoreCircle } from './UniversalScoreCircle';
import { cn } from '@/lib/utils';
import { colors } from '@/theme/colors';

// Unified Transaction List that consolidates:
// - TransactionList.tsx
// - VueniUnifiedTransactionList.tsx
// - OptimizedTransactionList.tsx
// - EnterpriseTransactionView.tsx
// - TransactionMain.tsx
// - TransactionWithScores.tsx

export interface Transaction {
  id: string;
  merchant: string;
  category: {
    name: string;
    color: string;
  };
  amount: number;
  date: string;
  status: 'completed' | 'pending' | 'failed';
  scores?: {
    health: number;
    eco: number;
    financial: number;
  };
  shipping?: {
    trackingNumber: string;
    provider: string;
    status: string;
  };
}

export type TransactionVariant = 'default' | 'clean' | 'enterprise' | 'apple' | 'modern' | 'compact';
export type SortField = 'date' | 'amount' | 'merchant' | 'category';
export type SortDirection = 'asc' | 'desc';

interface TransactionFeatures {
  searchable?: boolean;
  filterable?: boolean;
  sortable?: boolean;
  groupByDate?: boolean;
  showCategories?: boolean;
  showScores?: boolean;
  showStatus?: boolean;
  exportable?: boolean;
  compactMode?: boolean;
}

interface UnifiedTransactionListProps {
  transactions: Transaction[];
  variant?: TransactionVariant;
  currency?: string;
  features?: TransactionFeatures;
  onTransactionClick?: (transaction: Transaction) => void;
  onCategoryFilter?: (category: string) => void;
  onExport?: () => void;
  className?: string;
  maxHeight?: string;
}

const defaultFeatures: TransactionFeatures = {
  searchable: true,
  filterable: true,
  sortable: true,
  groupByDate: true,
  showCategories: true,
  showScores: true,
  showStatus: true,
  exportable: false,
  compactMode: false,
};

export const UnifiedTransactionList = React.memo<UnifiedTransactionListProps>(({
  transactions,
  variant = 'default',
  currency = 'USD',
  features = defaultFeatures,
  onTransactionClick,
  onCategoryFilter,
  onExport,
  className = '',
  maxHeight = '32rem'
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [sortField, setSortField] = useState<SortField>('date');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');
  const [isCollapsed, setIsCollapsed] = useState(false);

  const mergedFeatures = { ...defaultFeatures, ...features };

  // Memoized filtered and sorted transactions
  const processedTransactions = useMemo(() => {
    let filtered = transactions;

    // Search filter
    if (mergedFeatures.searchable && searchTerm) {
      filtered = filtered.filter(t => 
        t.merchant.toLowerCase().includes(searchTerm.toLowerCase()) ||
        t.category.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Category filter  
    if (mergedFeatures.filterable && selectedCategory !== 'all') {
      filtered = filtered.filter(t => t.category.name === selectedCategory);
    }

    // Sort
    if (mergedFeatures.sortable) {
      filtered.sort((a, b) => {
        let comparison = 0;

        switch (sortField) {
          case 'date':
            comparison = new Date(a.date).getTime() - new Date(b.date).getTime();
            break;
          case 'amount':
            comparison = Math.abs(a.amount) - Math.abs(b.amount);
            break;
          case 'merchant':
            comparison = a.merchant.localeCompare(b.merchant);
            break;
          case 'category':
            comparison = a.category.name.localeCompare(b.category.name);
            break;
        }

        return sortDirection === 'asc' ? comparison : -comparison;
      });
    }

    return filtered;
  }, [transactions, searchTerm, selectedCategory, sortField, sortDirection, mergedFeatures]);

  // Memoized grouped transactions for date grouping
  const groupedTransactions = useMemo(() => {
    if (!mergedFeatures.groupByDate) return { ['']: processedTransactions };

    return processedTransactions.reduce((groups, transaction) => {
      const date = new Date(transaction.date).toDateString();
      if (!groups[date]) {
        groups[date] = [];
      }
      groups[date].push(transaction);
      return groups;
    }, {} as Record<string, Transaction[]>);
  }, [processedTransactions, mergedFeatures.groupByDate]);

  // Memoized categories for filter dropdown
  const categories = useMemo(() => {
    const uniqueCategories = Array.from(
      new Set(transactions.map(t => t.category.name))
    );
    return uniqueCategories;
  }, [transactions]);

  // Optimized event handlers
  const handleSearchChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  }, []);

  const handleCategoryChange = useCallback((category: string) => {
    setSelectedCategory(category);
    onCategoryFilter?.(category);
  }, [onCategoryFilter]);

  const handleTransactionClick = useCallback((transaction: Transaction) => {
    onTransactionClick?.(transaction);
  }, [onTransactionClick]);

  const handleSort = useCallback((field: SortField) => {
    if (sortField === field) {
      setSortDirection(prev => prev === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('desc');
    }
  }, [sortField]);

  // Variant-specific styles
  const getVariantStyles = () => {
    switch (variant) {
      case 'clean':
        return {
          container: 'bg-transparent border-0',
          item: 'hover:bg-white/5 border-b border-white/5 last:border-b-0',
          spacing: 'py-3 px-4'
        };
      case 'enterprise':
        return {
          container: 'bg-slate-900/50 border border-slate-700',
          item: 'hover:bg-slate-800/50 border-b border-slate-700 last:border-b-0',
          spacing: 'py-4 px-6'
        };
      case 'apple':
        return {
          container: 'bg-white/5 backdrop-blur-md border border-white/10',
          item: 'hover:bg-white/8 border-b border-white/10 last:border-b-0',
          spacing: 'py-4 px-5'
        };
      case 'modern':
        return {
          container: 'bg-gradient-to-b from-white/10 to-white/5 border border-white/20',
          item: 'hover:bg-white/10 border-b border-white/10 last:border-b-0',
          spacing: 'py-4 px-5'
        };
      case 'compact':
        return {
          container: 'bg-white/5 border border-white/10',
          item: 'hover:bg-white/8 border-b border-white/5 last:border-b-0',
          spacing: 'py-2 px-3'
        };
      default:
        return {
          container: 'bg-white/5 border border-white/20',
          item: 'hover:bg-white/8 border-b border-white/10 last:border-b-0',
          spacing: 'py-3 px-4'
        };
    }
  };

  const variantStyles = getVariantStyles();

  const formatDate = (date: string) => {
    const dateObj = new Date(date);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (dateObj.toDateString() === today.toDateString()) {
      return 'Today';
    } else if (dateObj.toDateString() === yesterday.toDateString()) {
      return 'Yesterday';
    } else {
      return dateObj.toLocaleDateString('en-US', {
        weekday: 'short',
        month: 'short',
        day: 'numeric'
      });
    }
  };

  return (
    <UniversalCard 
      variant="glass" 
      className={cn('overflow-hidden', className, variantStyles.container)}
    >
      {/* Header with Search and Filters */}
      <div className="p-4 border-b border-white/10">
        <div className="flex items-center justify-between mb-4">
          <h3 className={cn(
            'font-semibold text-white',
            mergedFeatures.compactMode ? 'text-base' : 'text-lg'
          )}>
            Transactions
          </h3>
          <div className="flex items-center gap-2">
            <div className="text-sm text-white/60">
              {processedTransactions.length} of {transactions.length}
            </div>

            {mergedFeatures.exportable && onExport && (
              <button
                onClick={onExport}
                className="p-2 rounded-lg bg-white/10 hover:bg-white/20 transition-colors"
              >
                <Download className="w-4 h-4 text-white/70" />
              </button>
            )}

            <button
              onClick={() => setIsCollapsed(!isCollapsed)}
              className="p-2 rounded-lg bg-white/10 hover:bg-white/20 transition-colors"
            >
              {isCollapsed ? <Eye className="w-4 h-4 text-white/70" /> : <EyeOff className="w-4 h-4 text-white/70" />}
            </button>
          </div>
        </div>

        {!isCollapsed && (
          <>
            {/* Search and Filter Row */}
            <div className="flex gap-3 mb-3">
              {/* Search */}
              {mergedFeatures.searchable && (
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-white/60" />
                  <input
                    type="text"
                    placeholder="Search transactions..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full bg-white/10 border border-white/20 rounded-lg py-2 pl-10 pr-4 text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-blue-500/50"
                  />
                </div>
              )}

              {/* Category Filter */}
              {mergedFeatures.filterable && (
                <div className="relative">
                  <select
                    value={selectedCategory}
                    onChange={(e) => handleCategoryChange(e.target.value)}
                    className="bg-white/10 border border-white/20 rounded-lg py-2 pl-3 pr-8 text-white focus:outline-none focus:ring-2 focus:ring-blue-500/50 appearance-none"
                  >
                    <option value="all">All Categories</option>
                    {categories.map(category => (
                      <option key={category} value={category}>
                        {category}
                      </option>
                    ))}
                  </select>
                  <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 w-4 h-4 text-white/60 pointer-events-none" />
                </div>
              )}
            </div>

            {/* Sort Options */}
            {mergedFeatures.sortable && (
              <div className="flex gap-2">
                {(['date', 'amount', 'merchant', 'category'] as const).map(field => (
                  <button
                    key={field}
                    onClick={() => handleSort(field)}
                    className={cn(
                      'px-3 py-1 rounded-lg text-xs font-medium transition-colors',
                      sortField === field
                        ? 'bg-blue-500/20 text-blue-400'
                        : 'bg-white/10 text-white/60 hover:text-white/80'
                    )}
                  >
                    {field.charAt(0).toUpperCase() + field.slice(1)}
                    {sortField === field && (
                      sortDirection === 'asc' ? 
                        <TrendingUp className="inline ml-1 w-3 h-3" /> :
                        <TrendingDown className="inline ml-1 w-3 h-3" />
                    )}
                  </button>
                ))}
              </div>
            )}
          </>
        )}
      </div>

      {/* Transaction List */}
      {!isCollapsed && (
        <div 
          className="overflow-y-auto"
          style={{ maxHeight }}
        >
          {Object.entries(groupedTransactions).map(([date, groupTransactions]) => (
            <div key={date}>
              {/* Date Header (if grouping enabled) */}
              {mergedFeatures.groupByDate && date && (
                <div className="sticky top-0 bg-white/5 backdrop-blur-md px-4 py-2 border-b border-white/10">
                  <div className="text-sm font-medium text-white/80">
                    {formatDate(date)}
                  </div>
                </div>
              )}

              {/* Transactions in Group */}
              {groupTransactions.map((transaction) => (
                <TransactionItem
                  key={transaction.id}
                  transaction={transaction}
                  currency={currency}
                  features={mergedFeatures}
                  styles={variantStyles}
                  onClick={() => handleTransactionClick(transaction)}
                />
              ))}
            </div>
          ))}

          {/* Empty State */}
          {processedTransactions.length === 0 && (
            <div className="p-8 text-center">
              <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-white/5 flex items-center justify-center">
                <Calendar className="w-8 h-8 text-white/40" />
              </div>
              <div className="text-white/60 mb-2">No transactions found</div>
              <div className="text-sm text-white/40">
                {searchTerm || selectedCategory !== 'all' 
                  ? 'Try adjusting your search or filters'
                  : 'Your transactions will appear here'
                }
              </div>
            </div>
          )}
        </div>
      )}
    </UniversalCard>
  );
});

UnifiedTransactionList.displayName = 'UnifiedTransactionList';

// Memoized Transaction Item Component
const TransactionItem = React.memo<{
  transaction: Transaction;
  currency: string;
  features: TransactionFeatures;
  styles: ReturnType<typeof getVariantStyles>;
  onClick: () => void;
}>(({ transaction, currency, features, styles, onClick }) => {
  const formatAmount = (amount: number) => {
    const formatted = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency,
      minimumFractionDigits: 2
    }).format(Math.abs(amount));
    return amount < 0 ? `-${formatted}` : `+${formatted}`;
  };

  const getAmountColor = (amount: number) => {
    if (amount > 0) return colors.financial.positive;
    if (amount < 0) return colors.financial.negative;
    return colors.financial.neutral;
  };

  const getStatusIndicatorColor = (status: string) => {
    switch (status) {
      case 'completed': return colors.status.success;
      case 'pending': return colors.status.warning;
      case 'failed': return colors.status.error;
      default: return colors.status.neutral;
    }
  };

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div
      className={cn(
        'flex items-center justify-between cursor-pointer transition-colors',
        styles.item,
        styles.spacing
      )}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          onClick();
        }
      }}
    >
      {/* Left Side */}
      <div className="flex items-center space-x-3">
        {/* Status Indicator */}
        {features.showStatus && (
          <div 
            className="w-2 h-2 rounded-full flex-shrink-0"
            style={{ backgroundColor: getStatusIndicatorColor(transaction.status) }}
          />
        )}

