        </div>

        {/* Transaction Details */}
        <div className="space-y-1">
          <div className={cn(
            "font-medium text-white",
            features.compactMode ? "text-sm" : "text-base"
          )}>
            {transaction.merchant}
          </div>
          <div className="flex items-center space-x-2">
            {features.showCategories && (
              <span className="text-xs text-white/60">{transaction.category.name}</span>
            )}
            <span className="text-xs text-white/60">{formatDate(transaction.date)}</span>
          </div>
        </div>
      </div>

      {/* Right Side */}
      <div className="text-right space-y-1">
        {/* Amount */}
        <div className={cn(
          'font-semibold',
          features.compactMode ? 'text-sm' : 'text-base'
        )} style={{ color: getAmountColor(transaction.amount) }}>
          {formatAmount(transaction.amount)}
        </div>

        {/* Scores */}
        {features.showScores && transaction.scores && (
          <div className="flex space-x-1">
            <UniversalScoreCircle 
              score={transaction.scores.health} 
              type="health" 
              size={features.compactMode ? 'sm' : 'md'}
            />
            <UniversalScoreCircle 
              score={transaction.scores.eco} 
              type="eco" 
              size={features.compactMode ? 'sm' : 'md'}
            />
            <UniversalScoreCircle 
              score={transaction.scores.financial} 
              type="financial" 
              size={features.compactMode ? 'sm' : 'md'}
            />
          </div>
        )}
      </div>
    </div>
  );
});

