import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { secureStorage } from '@/utils/crypto';

interface LiquidGlassSettings {
  enabled: boolean;
  globalIntensity: number; // 0-1
  globalDistortion: number; // 0-1
  animated: boolean;
  interactive: boolean;
  performanceMode: boolean;
  autoDetectPerformance: boolean;
}

interface LiquidGlassContextType {
  settings: LiquidGlassSettings;
  updateSettings: (updates: Partial<LiquidGlassSettings>) => void;
  resetSettings: () => void;
  performanceInfo: {
    webGLSupported: boolean;
    isMobile: boolean;
    isLowEnd: boolean;
    prefersReducedMotion: boolean;
  };
}

const defaultSettings: LiquidGlassSettings = {
  enabled: true,
  globalIntensity: 0.6,
  globalDistortion: 0.4,
  animated: true,
  interactive: true,
  performanceMode: false,
  autoDetectPerformance: true,
};

const LiquidGlassContext = createContext<LiquidGlassContextType | undefined>(undefined);

// Performance detection utilities
const detectPerformance = () => {
  // Check WebGL support
  const checkWebGL = () => {
    try {
      const canvas = document.createElement('canvas');
      const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
      return !!gl;
    } catch (e) {
      return false;
    }
  };

  // Detect mobile devices
  const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
    navigator.userAgent
  );

  // Detect low-end devices
  const isLowEnd = navigator.hardwareConcurrency ? navigator.hardwareConcurrency < 4 : false;

  // Check reduced motion preference
  const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;

  return {
    webGLSupported: checkWebGL(),
    isMobile,
    isLowEnd,
    prefersReducedMotion,
  };
};

// Auto-adjust settings based on performance
const getOptimizedSettings = (
  settings: LiquidGlassSettings,
  performanceInfo: ReturnType<typeof detectPerformance>
): LiquidGlassSettings => {
  if (!settings.autoDetectPerformance) return settings;

  const { isMobile, isLowEnd, prefersReducedMotion, webGLSupported } = performanceInfo;

  // If no WebGL support, disable entirely
  if (!webGLSupported) {
    return { ...settings, enabled: false };
  }

  // Mobile optimization
  if (isMobile) {
    return {
      ...settings,
      globalIntensity: Math.min(settings.globalIntensity * 0.6, 0.4),
      globalDistortion: Math.min(settings.globalDistortion * 0.6, 0.3),
      animated: false,
      interactive: false,
    };
  }

  // Low-end device optimization
  if (isLowEnd) {
    return {
      ...settings,
      globalIntensity: Math.min(settings.globalIntensity * 0.7, 0.5),
      globalDistortion: Math.min(settings.globalDistortion * 0.7, 0.3),
      animated: !prefersReducedMotion,
    };
  }

  // Respect reduced motion preference
  if (prefersReducedMotion) {
    return {
      ...settings,
      animated: false,
    };
  }

  return settings;
};

export const LiquidGlassProvider = ({ children }: { children: ReactNode }) => {
  const [settings, setSettings] = useState<LiquidGlassSettings>(() => {
    // Load settings from secure storage if available
    try {
      const saved = secureStorage.getItem('vueni:liquidGlassSettings');
      return saved ? { ...defaultSettings, ...saved } : defaultSettings;
    } catch {
      return defaultSettings;
    }
  });

  const [performanceInfo] = useState(() => detectPerformance());

  // Get optimized settings based on performance
  const optimizedSettings = getOptimizedSettings(settings, performanceInfo);

  const updateSettings = (updates: Partial<LiquidGlassSettings>) => {
    const newSettings = { ...settings, ...updates };
    setSettings(newSettings);

    // Save to secure storage
    try {
      secureStorage.setItem('vueni:liquidGlassSettings', newSettings);
    } catch {
      // Ignore storage errors
    }
  };

  const resetSettings = () => {
    setSettings(defaultSettings);
    try {
      secureStorage.removeItem('vueni:liquidGlassSettings');
    } catch {
      // Ignore storage errors
    }
  };

  // Performance monitoring
  useEffect(() => {
    if (!optimizedSettings.enabled) return;

    let frameCount = 0;
    let lastTime = performance.now();
    let fps = 60;

    const monitorPerformance = () => {
      frameCount++;
      const currentTime = performance.now();

      if (currentTime - lastTime >= 1000) {
        fps = Math.round((frameCount * 1000) / (currentTime - lastTime));
        frameCount = 0;
        lastTime = currentTime;

        // Auto-enable performance mode if FPS drops too low
        if (fps < 30 && settings.autoDetectPerformance && !settings.performanceMode) {
          console.warn('Low FPS detected, enabling performance mode');
          updateSettings({ performanceMode: true });
        }
      }

      requestAnimationFrame(monitorPerformance);
    };

    const animationId = requestAnimationFrame(monitorPerformance);

    return () => {
      cancelAnimationFrame(animationId);
    };
  }, [optimizedSettings.enabled, settings.autoDetectPerformance, settings.performanceMode]);

  const contextValue: LiquidGlassContextType = {
    settings: optimizedSettings,
    updateSettings,
    resetSettings,
    performanceInfo,
  };

  return (
    <LiquidGlassContext.Provider value={contextValue}>
      {children}
    </LiquidGlassContext.Provider>
  );
};

export const useLiquidGlass = () => {
  const context = useContext(LiquidGlassContext);
  if (!context) {
    throw new Error('useLiquidGlass must be used within a LiquidGlassProvider');
  }
  return context;
};

// Hook for individual component settings
export const useLiquidGlassSettings = (
  componentSettings?: Partial<{
    intensity: number;
    distortion: number;
    animated: boolean;
    interactive: boolean;
  }>
) => {
  const { settings, performanceInfo } = useLiquidGlass();

  return {
    enabled: settings.enabled && performanceInfo.webGLSupported,
    intensity: componentSettings?.intensity ?? settings.globalIntensity,
    distortion: componentSettings?.distortion ?? settings.globalDistortion,
    animated: (componentSettings?.animated ?? settings.animated) && !performanceInfo.prefersReducedMotion,
    interactive: componentSettings?.interactive ?? settings.interactive,
    performanceMode: settings.performanceMode,
  };
};

import type { Transaction } from '@/types/shared';

export interface VueniCacheItem<T = unknown> {
  data: T;
  timestamp: number;
  expiresAt: number;
  key: string;
  size?: number;
  accessCount: number;
  lastAccessed: number;
}

export interface VueniCacheConfig {
  maxSize: number; // Maximum cache size in bytes
  maxAge: number; // Default TTL in milliseconds
  maxItems: number; // Maximum number of items
  enableCompression: boolean;
  enableMetrics: boolean;
  defaultTTL: number;
  cleanupInterval: number;
  compressionThreshold: number;
  enablePersistence: boolean;
  persistenceKey: string;
}

export class VueniCacheManager {
  private cache = new Map<string, VueniCacheItem>();
  private config: VueniCacheConfig;
  private currentSize = 0;
  private metrics = {
    hits: 0,
    misses: 0,
    evictions: 0,
    compressionSavings: 0
  };

  constructor(config: Partial<VueniCacheConfig> = {}) {
    this.config = {
      maxSize: 50 * 1024 * 1024, // 50MB default
      maxAge: 30 * 60 * 1000, // 30 minutes default
      maxItems: 1000,
      enableCompression: true,
      enableMetrics: true,
      defaultTTL: 30 * 60 * 1000, // 30 minutes default
      cleanupInterval: 5 * 60 * 1000, // 5 minutes
      compressionThreshold: 1024,
      enablePersistence: true,
      persistenceKey: 'vueni_cache',
      ...config
    };

    // Cleanup expired items periodically
    setInterval(() => this.cleanup(), this.config.cleanupInterval);
  }

  set<T>(key: string, data: T, ttl?: number): void {
    const timestamp = Date.now();
    const expiresAt = timestamp + (ttl || this.config.maxAge);

    // Estimate size
    const dataSize = this.estimateSize(data);

    // Compress data if enabled and beneficial
    let processedData = data;
    if (this.config.enableCompression && dataSize > this.config.compressionThreshold) { // Compress if > 1KB
      processedData = this.compress(data);
      const compressedSize = this.estimateSize(processedData);
      this.metrics.compressionSavings += dataSize - compressedSize;
    }

    const item: VueniCacheItem<T> = {
      data: processedData,
      timestamp,
      expiresAt,
      key,
      size: dataSize,
      accessCount: 0,
      lastAccessed: timestamp
    };

    // Remove old item if exists
    if (this.cache.has(key)) {
      const oldItem = this.cache.get(key)!;
      this.currentSize -= oldItem.size || 0;
    }

    // Check cache limits and evict if necessary
    this.ensureCacheSpace(dataSize);

    this.cache.set(key, item);
    this.currentSize += dataSize;

    if (this.config.enableMetrics) {
      console.log(`[Vueni Cache] Cached ${key} (${this.formatSize(dataSize)})`);
    }
  }

  get<T>(key: string): T | null {
    const item = this.cache.get(key) as VueniCacheItem<T> | undefined;

    if (!item) {
      this.metrics.misses++;
      return null;
    }

    // Check if expired
    if (Date.now() > item.expiresAt) {
      this.delete(key);
      this.metrics.misses++;
      return null;
    }

    // Update access metrics
    item.accessCount++;
    item.lastAccessed = Date.now();
    this.metrics.hits++;

    // Decompress if needed
    let data = item.data;
    if (this.isCompressed(data)) {
      data = this.decompress(data);
    }

    return data;
  }

  delete(key: string): boolean {
    const item = this.cache.get(key);
    if (item) {
      this.currentSize -= item.size || 0;
      return this.cache.delete(key);
    }
    return false;
  }

  clear(): void {
    this.cache.clear();
    this.currentSize = 0;
    console.log('[Vueni Cache] Cache cleared');
  }

  // Cache financial transactions with optimized strategy
  cacheTransactions(transactions: Transaction[], userId: string): void {
    const key = `vueni_transactions_${userId}`;

    // Optimize transaction data for caching
    const optimizedTransactions = transactions.map(transaction => ({
      id: transaction.id,
      merchant: transaction.merchant,
      category: transaction.category,
      amount: transaction.amount,
      date: transaction.date,
      status: transaction.status,
      scores: transaction.scores
    }));

    this.set(key, optimizedTransactions, 10 * 60 * 1000); // 10 minutes for transactions
  }

  getCachedTransactions(userId: string): Transaction[] | null {
    const key = `vueni_transactions_${userId}`;
    return this.get(key);
  }

  // Cache insights with longer TTL
  cacheInsights<T = Record<string, unknown>>(insights: T, userId: string): void {
    const key = `vueni_insights_${userId}`;
    this.set(key, insights, 60 * 60 * 1000); // 1 hour for insights
  }

  getCachedInsights<T = Record<string, unknown>>(userId: string): T | null {
    const key = `vueni_insights_${userId}`;
    return this.get(key);
  }

  // Cache component state
  cacheComponentState<T = Record<string, unknown>>(componentName: string, state: T): void {
    const key = `vueni_component_${componentName}`;
    this.set(key, state, 5 * 60 * 1000); // 5 minutes for component state
  }

  getCachedComponentState<T = Record<string, unknown>>(componentName: string): T | null {
    const key = `vueni_component_${componentName}`;
    return this.get(key);
  }

  private ensureCacheSpace(requiredSize: number): void {
    // If we exceed max items, evict oldest items
    if (this.cache.size >= this.config.maxItems) {
      this.evictLRU(1);
    }

    // If we exceed max size, evict until we have enough space
    while (this.currentSize + requiredSize > this.config.maxSize && this.cache.size > 0) {
      this.evictLRU(1);
    }
  }

  private evictLRU(count: number): void {
    const items = Array.from(this.cache.values())
      .sort((a, b) => a.lastAccessed - b.lastAccessed)
      .slice(0, count);

    items.forEach(item => {
      this.delete(item.key);
      this.metrics.evictions++;
    });

    if (this.config.enableMetrics && items.length > 0) {
      console.log(`[Vueni Cache] Evicted ${items.length} items (LRU)`);
    }
  }

  private cleanup(): void {
    const now = Date.now();
    const expiredKeys: string[] = [];

    this.cache.forEach((item, key) => {
      if (now > item.expiresAt) {
        expiredKeys.push(key);
      }
    });

    expiredKeys.forEach(key => this.delete(key));

    if (this.config.enableMetrics && expiredKeys.length > 0) {
      console.log(`[Vueni Cache] Cleaned up ${expiredKeys.length} expired items`);
    }
  }

  private estimateSize(data: unknown): number {
    if (typeof data === 'string') {
      return data.length * 2; // UTF-16 encoding
    }

    if (typeof data === 'number') {
      return 8; // 64-bit number
    }

    if (typeof data === 'boolean') {
      return 4;
    }

    if (data === null || data === undefined) {
      return 0;
    }

    if (Array.isArray(data)) {
      return data.reduce((total, item) => total + this.estimateSize(item), 0);
    }

    if (typeof data === 'object') {
      return Object.entries(data).reduce((total, [key, value]) => {
        return total + this.estimateSize(key) + this.estimateSize(value);
      }, 0);
    }

    return JSON.stringify(data).length * 2; // Fallback
  }

  private compress(data: unknown): unknown {
    // Simple compression simulation (in real implementation, use a compression library)
    if (typeof data === 'object') {
      const compressed = {
        __compressed: true,
        data: JSON.stringify(data)
      };
      return compressed;
    }
    return data;
  }

  private decompress(data: unknown): unknown {
    if (this.isCompressed(data)) {
      return JSON.parse(data.data);
    }
    return data;
  }

  private isCompressed(data: unknown): data is { __compressed: true; data: string } {
    return typeof data === 'object' && data.__compressed === true;
  }

  private formatSize(bytes: number): string {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(1)}${units[unitIndex]}`;
  }

  // Performance and monitoring methods
  getStats() {
    return {
      size: this.cache.size,
      currentSize: this.formatSize(this.currentSize),
      maxSize: this.formatSize(this.config.maxSize),
      hitRate: (this.metrics.hits / (this.metrics.hits + this.metrics.misses) * 100).toFixed(1) + '%',
      metrics: this.metrics,
      compressionSavings: this.formatSize(this.metrics.compressionSavings)
    };
  }

  // Export cache contents for debugging
  exportCache(): any {
    const items: any[] = [];
    this.cache.forEach((item, key) => {
      items.push({
        key,
        size: this.formatSize(item.size || 0),
        expiresIn: Math.max(0, item.expiresAt - Date.now()),
        accessCount: item.accessCount,
        lastAccessed: new Date(item.lastAccessed).toISOString()
      });
    });

    return {
      stats: this.getStats(),
      items: items.sort((a, b) => b.accessCount - a.accessCount)
    };
  }

  // Preload commonly accessed data
  preloadCommonData(): void {
    // Preload data that's commonly accessed
    const commonKeys = [
      'vueni_user_preferences',
      'vueni_category_mappings',
      'vueni_feature_flags'
    ];

    commonKeys.forEach(key => {
      // Check if data exists in localStorage and cache it
      const data = localStorage.getItem(key);
      if (data) {
        try {
          const parsedData = JSON.parse(data);
          this.set(key, parsedData, 60 * 60 * 1000); // 1 hour
        } catch (error) {
          console.warn(`[Vueni Cache] Failed to preload ${key}:`, error);
        }
      }
    });
  }
}

