  private mapPlaidAccountType(plaidType: string): Account['accountType'] {
    switch (plaidType) {
      case 'depository':
        return 'depository';
      case 'credit':
        return 'credit';
      case 'loan':
        return 'loan';
      case 'investment':
        return 'investment';
