test.describe('Performance Validation', () => {
  test('should meet Core Web Vitals targets', async ({ page }) => {
    await page.goto('http://localhost:5173/');

    // Navigate to planning which has the most complex UI
    await page.getByRole('button', { name: /planning/i }).click();

    // Measure LCP (Largest Contentful Paint) - should be < 2.5s
    const lcp = await page.evaluate(() => {
      return new Promise((resolve) => {
        new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - 1];
          resolve(lastEntry.startTime);
        }).observe({ entryTypes: ['largest-contentful-paint'] });

        // Fallback timeout
        setTimeout(() => resolve(0), 3000);
      });
    });

    if (typeof lcp === 'number' && lcp > 0) {
      expect(lcp).toBeLessThan(2500); // 2.5 seconds
    }

    // Check that there are no excessive reflows/repaints
    await page.getByRole('button', { name: 'Retirement' }).click();
    await page.waitForTimeout(500);

    // The page should be stable (no layout shifts after loading)
    const isStable = await page.evaluate(() => {
      return new Promise((resolve) => {
        let cumulativeShift = 0;
        new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            if (entry.entryType === 'layout-shift' && !(entry as any).hadRecentInput) {
              cumulativeShift += (entry as any).value;
            }
          }
        }).observe({ entryTypes: ['layout-shift'] });

        setTimeout(() => {
          resolve(cumulativeShift < 0.1); // CLS should be < 0.1
        }, 1000);
      });
    });

    expect(isStable).toBe(true);
  });
});

import { chromium, FullConfig } from '@playwright/test';
import fs from 'fs';
import path from 'path';

/**
 * Playwright globalSetup
 * Crawls the application starting at the root route and builds a manifest of all
 * interactive elements (anchor, button, tab, or element with an onclick attr).
 * The resulting JSON is written to the project root as `ui-map.json` and is
 * used by `e2e/all-interactions.spec.ts` to drive exhaustive UI interaction tests.
 */
export default async function globalSetup(_: FullConfig) {
  // Base URL will usually come from the shared `use.baseURL` setting.
  const baseURL = process.env.BASE_URL || 'http://localhost:5173';

  // Launch a headless Chromium instance for fast crawling.
  const browser = await chromium.launch();
  const context = await browser.newContext();
  const page = await context.newPage();

  // Track where we've been and where we still need to visit â€“ root first.
  const visited = new Set<string>();
  const queue: string[] = ['/'];

  type ManifestEntry = {
    selector: string;
    type: 'a' | 'button' | 'tab' | 'onclick';
    originPage: string;
    expectUrlChange: boolean;
  };
  const manifest: ManifestEntry[] = [];

  while (queue.length) {
    const currentPath = queue.shift() as string;
    if (visited.has(currentPath)) continue;
    visited.add(currentPath);

    try {
      await page.goto(`${baseURL}${currentPath}`, { waitUntil: 'domcontentloaded' });
    } catch (err) {
      // If navigation fails, skip this route and continue.
      /* eslint-disable no-console */
      console.warn(`[playwright-audit] Failed to load ${currentPath}:`, err);
      continue;
    }

    // Grab interactive DOM elements on the page.
    const entries: ManifestEntry[] = await page.evaluate(() => {
      function buildSelector(el: Element): string | null {
        // Prefer id
        if (el.id) return `#${el.id}`;
        // Fallback: text content (first 40 characters) for stability with Playwright's text selector.
        const text = el.textContent?.trim();
        if (text) return `text=${text.substring(0, 40)}`;
        return null;
      }

      const elements = Array.from(document.querySelectorAll('a, button, [role="tab"], [onclick]'));
      return elements
        .map((el) => {
          const tag = el.tagName.toLowerCase();
          const selector = buildSelector(el);
          if (!selector) return null;

          let type: ManifestEntry['type'] = tag as ManifestEntry['type'];
          if (el.getAttribute('role') === 'tab') type = 'tab';
          if (el.hasAttribute('onclick')) type = 'onclick';

          const hrefAttr = (el as HTMLAnchorElement).getAttribute?.('href');
          const expectUrlChange = !!hrefAttr && hrefAttr !== '#' && !hrefAttr?.startsWith('javascript:');

          return { selector, type, originPage: window.location.pathname, expectUrlChange } as ManifestEntry;
        })
        .filter(Boolean) as ManifestEntry[];
    });

    // Append to manifest and queue up new routes found via anchors.
    for (const entry of entries) {
      manifest.push(entry);

      // If the element is a link to same-origin path, queue it for crawling.
      const href = (await page.locator(entry.selector).getAttribute('href')) || undefined;
      if (href && href.startsWith('/') && !visited.has(href) && !queue.includes(href)) {
        queue.push(href);
      }
    }
  }

  await browser.close();

  // Persist manifest to the repository root so the spec file can import it.
  const outPath = path.resolve(process.cwd(), 'ui-map.json');
  fs.writeFileSync(outPath, JSON.stringify(manifest, null, 2));
  console.log(`ðŸ—ºï¸  UI interaction map generated with ${manifest.length} entries -> ${outPath}`);
}

import { expect, Page, Locator } from '@playwright/test';

// Hook violation detection utilities
export class HookValidationMonitor {
  private page: Page;
  private errors: string[] = [];
  private hookCounts: Map<string, number> = new Map();

  constructor(page: Page) {
    this.page = page;
  }

  // Monitor console for React hook errors
  async startMonitoring(): Promise<void> {
    this.page.on('console', (message) => {
      const text = message.text();
      if (text.includes('rendered more hooks') || 
          text.includes('hook') && text.includes('error') ||
          text.includes('useEffect') && text.includes('error') ||
          text.includes('useState') && text.includes('error')) {
        this.errors.push(text);
        console.error('Hook validation error detected:', text);
      }
    });

    this.page.on('pageerror', (error) => {
      if (error.message.includes('hook')) {
        this.errors.push(error.message);
        console.error('Page error related to hooks:', error.message);
      }
    });
  }

  // Check for hook violations
  getErrors(): string[] {
    return [...this.errors];
  }

  hasHookViolations(): boolean {
    return this.errors.length > 0;
  }

  // Reset monitoring
  reset(): void {
    this.errors = [];
    this.hookCounts.clear();
  }

  // Get performance metrics
  async getPerformanceMetrics() {
    return await this.page.evaluate(() => {
      const navigationTiming = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
      return {
        loadTime: navigationTiming.loadEventEnd - navigationTiming.loadEventStart,
        domContentLoaded: navigationTiming.domContentLoadedEventEnd - navigationTiming.domContentLoadedEventStart,
        firstPaint: performance.getEntriesByType('paint').find(entry => entry.name === 'first-paint')?.startTime || 0,
        firstContentfulPaint: performance.getEntriesByType('paint').find(entry => entry.name === 'first-contentful-paint')?.startTime || 0
      };
    });
  }
}

// Navigation test utilities
export class NavigationTester {
  private page: Page;
  private monitor: HookValidationMonitor;

  constructor(page: Page, monitor: HookValidationMonitor) {
    this.page = page;
    this.monitor = monitor;
  }

  // Test navigation between tabs
  async navigateToTab(tabName: string): Promise<void> {
    const tabButton = this.page.locator(`[data-testid="nav-${tabName}"], button:has-text("${tabName}"), a:has-text("${tabName}")`).first();

    await expect(tabButton).toBeVisible();
    await tabButton.click();

    // Wait for navigation to complete
    await this.page.waitForTimeout(500);

    // Check for hook violations after navigation
    if (this.monitor.hasHookViolations()) {
      throw new Error(`Hook violations detected after navigating to ${tabName}: ${this.monitor.getErrors().join(', ')}`);
    }
  }

  // Test rapid navigation
  async testRapidNavigation(tabs: string[]): Promise<void> {
    for (const tab of tabs) {
      await this.navigateToTab(tab);
      await this.page.waitForTimeout(100); // Brief pause between navigations
    }
  }

  // Test URL parameter changes
  async testUrlParameterNavigation(tab: string): Promise<void> {
    await this.page.goto(`/?tab=${tab}`);
    await this.page.waitForLoadState('networkidle');

    if (this.monitor.hasHookViolations()) {
      throw new Error(`Hook violations detected with URL parameter navigation to ${tab}: ${this.monitor.getErrors().join(', ')}`);
    }
  }
}

// Component validation utilities
export class ComponentValidator {
  private page: Page;

  constructor(page: Page) {
    this.page = page;
  }

  // Validate component is rendered and functional
  async validateComponent(selector: string, componentName: string): Promise<void> {
    const component = this.page.locator(selector);
    await expect(component).toBeVisible({ timeout: 10000 });

    // Check if component is interactive
    const isInteractive = await component.isEnabled();
    if (!isInteractive) {
      console.warn(`Component ${componentName} is not interactive`);
    }
  }

  // Check for React dev tools markers
  async hasReactComponents(): Promise<boolean> {
    return await this.page.evaluate(() => {
      // Check for React fiber nodes or dev tools
      const hasReactFiber = document.querySelector('[data-reactroot]') !== null;
      const hasReactDevTools = (window as unknown as { __REACT_DEVTOOLS_GLOBAL_HOOK__?: unknown }).__REACT_DEVTOOLS_GLOBAL_HOOK__ !== undefined;
      return hasReactFiber || hasReactDevTools;
    });
  }

  // Validate specific UI elements exist
  async validateUIElements(elements: { selector: string; name: string }[]): Promise<void> {
    for (const element of elements) {
      await expect(this.page.locator(element.selector)).toBeVisible({ 
        timeout: 5000 
      });
    }
  }
}

// Memory and performance testing
export class PerformanceTester {
  private page: Page;

  constructor(page: Page) {
    this.page = page;
  }

  // Memory pressure test
  async memoryPressureTest(): Promise<void> {
    await this.page.evaluate(() => {
      // Create memory pressure
      const arrays = [];
      for (let i = 0; i < 100; i++) {
        arrays.push(new Array(10000).fill(Math.random()));
      }

      // Force garbage collection if available
      const windowWithGC = window as unknown as { gc?: () => void };
      if (windowWithGC.gc) {
        windowWithGC.gc();
      }
    });
  }

  // CPU stress test
  async cpuStressTest(): Promise<void> {
    await this.page.evaluate(() => {
      // CPU intensive operation
      const start = Date.now();
      let cpuWork = 0;
      while (Date.now() - start < 1000) {
        cpuWork += Math.random() * Math.random();
      }
    });
  }

  // Network throttling simulation
  async simulateSlowNetwork(): Promise<void> {
    await this.page.route('**/*', async route => {
      await new Promise(resolve => setTimeout(resolve, 100)); // 100ms delay
      await route.continue();
    });
  }
}

