    CLS: 0.05,      // Cumulative Layout Shift  
    FCP: 1800,      // First Contentful Paint
    TTI: 2500,      // Time to Interactive
    TBT: 200,       // Total Blocking Time
    SI: 2500        // Speed Index
  },
  desktop: {
    LCP: 1500,
