        const contentLength = response.headers()['content-length'];
        if (contentLength) {
          responses.push({
            url: response.url(),
            size: parseInt(contentLength)
          });
        }
      }
    });

    await page.goto('/');

    // Check individual chunk sizes
    responses.forEach(response => {
      // No single chunk should exceed 500KB (compressed)
