          cls.includes('xl:') || cls.includes('2xl:') || cls.includes('max-w-')
        );

        if (hasResponsiveClasses) {
          responsiveClasses.push({
            tag: element.tagName,
            classes: classList.filter(cls => 
              cls.includes('sm:') || cls.includes('md:') || cls.includes('lg:') || 
              cls.includes('xl:') || cls.includes('2xl:') || cls.includes('max-w-')
            )
          });
