    test('should have sufficient color contrast', async ({ page }) => {
      // Check text contrast on glass morphism backgrounds
      const textElements = await page.locator('.backdrop-blur p, .backdrop-blur span').all();

      for (const element of textElements.slice(0, 5)) { // Check first 5 elements
        const color = await element.evaluate(el => window.getComputedStyle(el).color);
        const bgColor = await element.evaluate(el => {
          let parent = el.parentElement;
          while (parent) {
            const bg = window.getComputedStyle(parent).backgroundColor;
            if (bg !== 'rgba(0, 0, 0, 0)') return bg;
            parent = parent.parentElement;
          }
          return 'rgb(0, 0, 0)';
        });

        // Text should not be too transparent
        expect(color).not.toMatch(/rgba\(.*,.*,.*,\s*0\.[0-4]/);
