  test('should verify Vueni design system components', async ({ page }) => {
    // Check for design system elements
    const designSystemElements = [
      '.vueni-glass-card, [class*="glass"]',
      '.vueni-button, button',
      '.vueni-metric, [data-testid*="metric"]',
