import React, { useState, useEffect, useRef } from 'react';
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from '@/shared/ui/sheet';
import { Button } from '@/shared/ui/button';
import GlassCard from '@/components/GlassCard';
import { MessageCircle, Send, Sparkles, X } from 'lucide-react';
import { mockAiService, ChatMessage } from '@/features/mockAiService';

interface ChatDrawerProps {
  userContext?: Record<string, unknown>;
}

const ChatDrawer = ({ userContext }: ChatDrawerProps) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: ChatMessage = {
      id: `msg_${Date.now()}`,
      role: 'user',
      content: inputValue.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      const response = await mockAiService.sendMessage(userMessage.content, userContext);
      setMessages(prev => [...prev, response]);
    } catch (error) {
      console.error('Failed to send message:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const quickActions = [
    'Help me create a budget',
    'Analyze my spending patterns',
    'Set a savings goal',
    'Review my subscriptions'
  ];

  return (
    <Sheet open={isOpen} onOpenChange={setIsOpen}>
      <SheetTrigger asChild>
        <GlassCard
          className="fixed bottom-32 right-6 p-4 glass-interactive z-40 glass-fab"
          interactive
          shimmer
        >
          <MessageCircle className="w-6 h-6 text-white" />
        </GlassCard>
      </SheetTrigger>

      <SheetContent className="w-full sm:max-w-md bg-gradient-to-br from-[#0A0A0B] via-[#1C1C1E] to-[#0A0A0B] border-white/10">
        <SheetHeader className="pb-4">
          <SheetTitle className="text-white flex items-center gap-2">
            <Sparkles className="w-5 h-5 text-blue-400" />
            Financial Assistant
          </SheetTitle>
        </SheetHeader>

        <div className="flex flex-col h-[calc(100vh-120px)]">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto space-y-4 pr-2">
            {messages.length === 0 && (
              <div className="text-center py-8">
                <Sparkles className="w-12 h-12 text-blue-400 mx-auto mb-4 opacity-50" />
                <p className="text-white/70 mb-4">Hi! I'm your financial assistant. I can help you with budgeting, spending analysis, and financial goals.</p>

                <div className="space-y-2">
                  <p className="text-white/50 text-sm">Try asking:</p>
                  {quickActions.map((action, index) => (
                    <button
                      key={index}
                      onClick={() => setInputValue(action)}
                      className="block w-full text-left p-2 text-sm text-blue-400 hover:bg-white/5 rounded-lg transition-colors"
                    >
                      "{action}"
                    </button>
                  ))}
                </div>
              </div>
            )}

            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <GlassCard
                  className={`max-w-[80%] p-3 ${
                    message.role === 'user'
                      ? 'glass-blue text-white'
                      : 'glass-secondary text-white'
                  }`}
                >
                  <p className="text-sm">{message.content}</p>
                  <p className="text-xs text-white/50 mt-1">
                    {message.timestamp.toLocaleTimeString([], { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </p>
                </GlassCard>
              </div>
            ))}

            {isLoading && (
              <div className="flex justify-start">
                <GlassCard className="glass-secondary p-3">
                  <div className="flex items-center gap-2">
                    <div className="flex gap-1">
                      <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse"></div>
                      <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
                      <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse" style={{ animationDelay: '0.4s' }}></div>
                    </div>
                    <span className="text-white/70 text-sm">Thinking...</span>
                  </div>
                </GlassCard>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="pt-4 border-t border-white/10">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                placeholder="Ask about your finances..."
                className="flex-1 bg-white/5 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/50 focus:outline-none focus:border-blue-400 focus:bg-white/10"
                disabled={isLoading}
              />
              <Button
                onClick={handleSendMessage}
                disabled={!inputValue.trim() || isLoading}
                className="bg-blue-500 hover:bg-blue-600 text-white p-2"
              >
                <Send className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
};

export default ChatDrawer;

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Send, 
  Bot, 
  User, 
  Lightbulb, 
  TrendingUp, 
  Target, 
  AlertCircle,
  Sparkles,
  MessageCircle,
  Trash2,
  RotateCcw
} from 'lucide-react';
import { aiFinancialService } from '@/features/aiFinancialService';
import { familyService } from '@/features/familyService';
import { accountService } from '@/features/accounts/api/accountService';
import { transactionService } from '@/features/transactions/api/transactionService';
import { cn } from '@/shared/lib/utils';

interface FinancialAIChatProps {
  familyId: string;
  className?: string;
  compact?: boolean;
}

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  suggestions?: string[];
}

interface FinancialContext {
  totalBalance: number;
  monthlySpending: number;
  savingsGoals: number;
  creditScore: number;
  investments: number;
  recentTransactions: Array<{
    id: string;
    amount: number;
    description: string;
    category: string;
  }>;
}

const FinancialAIChat = ({ familyId, className, compact = false }: FinancialAIChatProps) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [currentInput, setCurrentInput] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [financialContext, setFinancialContext] = useState<FinancialContext | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  const loadChatHistory = useCallback(async () => {
    try {
      const history = await aiFinancialService.getChatHistory(familyId);
      setMessages(history);
    } catch (error) {
      console.error('Failed to load chat history:', error);
    }
  }, [familyId]);

  const loadFinancialContext = useCallback(async () => {
    try {
      const [family, accounts, budgets, goals] = await Promise.all([
        familyService.getFamilyData(familyId),
        accountService.getAccountSummary(familyId),
        budgetService.getBudgetSummary(familyId),
        savingsGoalsService.getUserGoals(familyId)
      ]);

      setFinancialContext({
        totalBalance: accounts.totalBalance,
        monthlySpending: budgets.totalSpent,
        savingsGoals: goals.length,
        creditScore: 750, // This would come from credit service
        investments: accounts.totalInvestments || 0,
        recentTransactions: accounts.recentTransactions || []
      });
    } catch (error) {
      console.error('Failed to load financial context:', error);
    }
  }, [familyId]);

  useEffect(() => {
    loadChatHistory();
    loadFinancialContext();
  }, [loadChatHistory, loadFinancialContext]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async () => {
    if (!currentInput.trim() || isLoading || !financialContext) return;

    const userMessage: ChatMessage = {
      id: `msg_${Date.now()}_user`,
      type: 'user',
      content: currentInput.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setCurrentInput('');
    setIsLoading(true);

    try {
      const response = await aiFinancialService.processFinancialQuery(
        familyId,
        currentInput.trim(),
        financialContext
      );

      const assistantMessage: ChatMessage = {
        id: `msg_${Date.now()}_assistant`,
        type: 'assistant',
        content: response.content,
        timestamp: new Date(),
        suggestions: response.recommendations
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      const errorMessage: ChatMessage = {
        id: `msg_${Date.now()}_error`,
        type: 'assistant',
        content: 'I apologize, but I encountered an error processing your request. Please try again.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const clearChatHistory = () => {
    aiFinancialService.clearChatHistory(familyId);
    setMessages([]);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatTimestamp = (timestamp: Date) => {
    return timestamp.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const getInsightIcon = (type: string) => {
    switch (type) {
      case 'spending_pattern':
        return <TrendingUp className="w-4 h-4" />;
      case 'savings_opportunity':
        return <Target className="w-4 h-4" />;
      case 'budget_analysis':
        return <AlertCircle className="w-4 h-4" />;
      case 'investment_advice':
        return <Sparkles className="w-4 h-4" />;
      default:
        return <Lightbulb className="w-4 h-4" />;
    }
  };

  const getInsightColor = (impact: string) => {
    switch (impact) {
      case 'high':
        return 'text-red-400 bg-red-500/20 border-red-500/30';
      case 'medium':
        return 'text-yellow-400 bg-yellow-500/20 border-yellow-500/30';
      case 'low':
        return 'text-green-400 bg-green-500/20 border-green-500/30';
      default:
        return 'text-blue-400 bg-blue-500/20 border-blue-500/30';
    }
  };

  const suggestedQuestions = [
    "How can I improve my savings rate?",
    "What should I focus on in my budget?",
    "Is my spending healthy this month?",
    "Should I invest more or pay down debt?",
    "How's my emergency fund looking?"
  ];

  if (!financialContext) {
    return (
      <div className={cn("flex items-center justify-center p-8", className)}>
        <div className="text-center">
          <Bot className="w-12 h-12 text-blue-400 mx-auto mb-4 animate-pulse" />
          <p className="text-white/60">Loading your financial context...</p>
        </div>
      </div>
    );
  }

  return (
    <div className={cn("flex flex-col h-full bg-white/[0.02] rounded-2xl border border-white/[0.08]", className)}>
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-white/[0.08]">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-blue-500/20 rounded-xl flex items-center justify-center">
            <Bot className="w-6 h-6 text-blue-400" />
          </div>
          <div>
            <h3 className="font-semibold text-white">AI Financial Advisor</h3>
            <p className="text-white/60 text-sm">
              {compact ? 'Ask me anything' : 'Personalized advice based on your financial data'}
            </p>
          </div>
        </div>

        {messages.length > 0 && (
          <div className="flex items-center gap-2">
            <button
              onClick={clearChatHistory}
              className="p-2 rounded-lg hover:bg-white/[0.05] transition-colors text-white/60 hover:text-white"
              title="Clear chat"
            >
              <Trash2 className="w-4 h-4" />
            </button>
          </div>
        )}
      </div>

      {/* Messages */}
      <div className={cn("flex-1 overflow-y-auto p-4 space-y-4", compact ? "max-h-96" : "min-h-[400px]")}>
        {messages.length === 0 ? (
          <div className="text-center py-8">
            <Bot className="w-16 h-16 text-blue-400/50 mx-auto mb-4" />
            <h4 className="text-white font-medium mb-2">Hi there! I'm your AI financial advisor.</h4>
            <p className="text-white/60 text-sm mb-6">
              I have access to your complete financial picture and can provide personalized advice.
            </p>

            <div className="space-y-2">
              <p className="text-white/80 text-sm font-medium">Try asking:</p>
              <div className="space-y-2">
                {suggestedQuestions.slice(0, compact ? 3 : 5).map((question, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentInput(question)}
                    className="block w-full text-left p-3 bg-white/[0.03] hover:bg-white/[0.05] border border-white/[0.05] rounded-lg text-white/80 hover:text-white text-sm transition-all"
                  >
                    "{question}"
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div key={message.id} className={cn("flex gap-3", message.type === 'user' ? 'justify-end' : 'justify-start')}>
              <div className={cn("flex gap-3 max-w-[80%]", message.type === 'user' ? 'flex-row-reverse' : 'flex-row')}>
                {/* Avatar */}
                <div className={cn(
                  "w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0",
                  message.type === 'user' 
                    ? "bg-green-500/20" 
                    : "bg-blue-500/20"
                )}>
                  {message.type === 'user' ? (
                    <User className="w-4 h-4 text-green-400" />
                  ) : (
                    <Bot className="w-4 h-4 text-blue-400" />
                  )}
                </div>

                {/* Message Content */}
                <div className={cn(
                  "flex flex-col gap-2",
                  message.type === 'user' ? 'items-end' : 'items-start'
                )}>
                  <div className={cn(
                    "rounded-2xl px-4 py-3 max-w-full",
                    message.type === 'user'
                      ? "bg-blue-500 text-white"
                      : "bg-white/[0.05] border border-white/[0.08] text-white"
                  )}>
                    <p className="text-sm leading-relaxed whitespace-pre-wrap">{message.content}</p>
                  </div>

