} from 'lucide-react';

export type TransactionVariant = 'default' | 'apple' | 'clean' | 'polished' | 'enterprise' | 'mobile';

export interface VueniTransaction {
  id: string;
  merchant: string;
  category: {
    name: string;
    color: string;
  };
  amount: number;
  date: string;
  status: 'completed' | 'pending' | 'failed';
  scores?: {
    health: number;
    eco: number;
    financial: number;
  };
  description?: string;
  reference?: string;
  tags?: string[];
}

export interface VueniTransactionFeatures {
  showScores?: boolean;
  showCategories?: boolean;
  searchable?: boolean;
  filterable?: boolean;
  compactMode?: boolean;
  animationsEnabled?: boolean;
  groupByDate?: boolean;
  virtualScrolling?: boolean;
  exportEnabled?: boolean;
}

export interface VueniUnifiedTransactionListProps {
  transactions: VueniTransaction[];
  variant?: TransactionVariant;
  features?: VueniTransactionFeatures;
  currency?: string;
  className?: string;
  onTransactionClick?: (transaction: VueniTransaction) => void;
  onTransactionAction?: (action: string, transaction: VueniTransaction) => void;
  onExportData?: () => void;
}

const defaultFeatures: VueniTransactionFeatures = {
  showScores: true,
  showCategories: true,
  searchable: true,
  filterable: true,
  compactMode: false,
  animationsEnabled: true,
  groupByDate: true,
  virtualScrolling: false,
  exportEnabled: true,
};

// Variant-specific styling configurations
const variantConfigs = {
