interface AccountLinkingProps {
  familyId: string;
  onAccountsLinked: (accounts: Account[]) => void;
  onClose: () => void;
}

const AccountLinking = ({ familyId, onAccountsLinked, onClose }: AccountLinkingProps) => {
  const [step, setStep] = useState<'search' | 'connect' | 'select' | 'success'>('search');
  const [institutions, setInstitutions] = useState<Institution[]>([]);
  const [selectedInstitution, setSelectedInstitution] = useState<Institution | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [linkedAccounts, setLinkedAccounts] = useState<Account[]>([]);
  const [isConnecting, setIsConnecting] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const loadInstitutions = useCallback(async () => {
    try {
      setLoading(true);
      const data = await accountService.getInstitutions(searchQuery);
      setInstitutions(data);
    } catch (error) {
      console.error('Failed to load institutions:', error);
    } finally {
      setLoading(false);
    }
  }, [searchQuery]);

  useEffect(() => {
    loadInstitutions();
  }, [loadInstitutions]);

  const handleInstitutionSelect = (institution: Institution) => {
    setSelectedInstitution(institution);
    setStep('connect');
  };

  const handlePlaidSuccess = async (publicToken: string, accounts: Array<{
    id: string;
    name: string;
    type: string;
    subtype: string;
  }>) => {
    if (!selectedInstitution) return;

    setLoading(true);
    try {
      const linkedAccounts = await accountService.linkAccountsViaPlaid({
        familyId,
        publicToken,
        institutionId: selectedInstitution.id,
        accounts: accounts.map(acc => ({
          id: acc.id,
          name: acc.name,
          type: acc.type,
          subtype: acc.subtype
        }))
      });

      setLinkedAccounts(linkedAccounts);
      setStep('success');
      onAccountsLinked(linkedAccounts);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to link accounts');
    } finally {
      setLoading(false);
    }
