interface SecurityState {
  isRateLimited: boolean;
  remainingRequests: number;
  lastCalculation: Date | null;
  securityLevel: 'normal' | 'elevated' | 'locked';
  consecutiveErrors: number;
}

interface ValidationResult {
  // Define the structure of the validation result
}

/**
 * SecureCalculatorWrapper - Wraps financial calculators with security measures
 */
export function SecureCalculatorWrapper({ 
  calculatorName, 
  children, 
  onSecurityEvent 
}: SecureCalculatorWrapperProps) {
  const [securityState, setSecurityState] = useState<SecurityState>({
    isRateLimited: false,
    remainingRequests: 100,
    lastCalculation: null,
    securityLevel: 'normal',
    consecutiveErrors: 0
  });

  const [showSecurityInfo, setShowSecurityInfo] = useState(false);

  // Check rate limits on component mount and at intervals
  useEffect(() => {
    const checkRateLimit = () => {
      const isLimited = security.rateLimit.isRateLimited(`calculator:${calculatorName}`);
      const remaining = security.rateLimit.getRemainingRequests(`calculator:${calculatorName}`);

      setSecurityState(prev => ({
        ...prev,
        isRateLimited: isLimited,
        remainingRequests: remaining
      }));

      if (isLimited) {
        VueniSecurityMonitoring.logEvent(
          'RATE_LIMIT_EXCEEDED',
          'medium',
          `Rate limit exceeded for calculator: ${calculatorName}`,
          { calculatorName, remainingRequests: remaining }
        );

        onSecurityEvent?.('rate_limit_exceeded', { calculatorName, remaining });
      }
    };

    checkRateLimit();
    const interval = setInterval(checkRateLimit, 60000); // Check every minute

    return () => clearInterval(interval);
  }, [calculatorName, onSecurityEvent]);

  // Handle security violations
  const handleSecurityViolation = (violationType: string, details: unknown) => {
    setSecurityState(prev => {
      const newConsecutiveErrors = prev.consecutiveErrors + 1;
      let newSecurityLevel = prev.securityLevel;

      // Escalate security level based on consecutive errors
      if (newConsecutiveErrors >= 10) {
        newSecurityLevel = 'locked';
      } else if (newConsecutiveErrors >= 5) {
        newSecurityLevel = 'elevated';
      }

      return {
        ...prev,
        consecutiveErrors: newConsecutiveErrors,
        securityLevel: newSecurityLevel
      };
    });

    VueniSecurityMonitoring.logEvent(
      'INVALID_INPUT',
      newSecurityLevel === 'locked' ? 'high' : 'medium',
      `Security violation in ${calculatorName}: ${violationType}`,
      { calculatorName, violationType, details }
    );

    onSecurityEvent?.('security_violation', { violationType, details });
  };

  // Reset security state on successful calculation
  const handleSuccessfulCalculation = () => {
    setSecurityState(prev => ({
      ...prev,
      lastCalculation: new Date(),
      consecutiveErrors: Math.max(0, prev.consecutiveErrors - 1),
      securityLevel: prev.consecutiveErrors <= 1 ? 'normal' : prev.securityLevel
    }));
  };

  // Provide security context to child components
  const securityContext = {
    sanitize: security.sanitize,
    validateInput: (type: string, value: string | number) => {
      try {
        switch (type) {
          case 'amount':
            return security.sanitize.sanitizeFinancialAmount(value);
          case 'percentage':
            return security.sanitize.sanitizePercentage(value);
          case 'interestRate':
            return security.sanitize.sanitizeInterestRate(value);
          case 'year':
            return security.sanitize.sanitizeYear(value);
          case 'timePeriod':
            return security.sanitize.sanitizeTimePeriod(value);
          case 'text':
            return security.sanitize.sanitizeText(value);
          default:
            return value;
        }
      } catch (error) {
        handleSecurityViolation('invalid_input', { type, value, error: error.message });
        throw error;
      }
    },
    onCalculationSuccess: handleSuccessfulCalculation,
    onCalculationError: (error: Error) => {
      handleSecurityViolation('calculation_error', { error: error.message });
    },
    securityLevel: securityState.securityLevel
  };

  // Render security locked state
  if (securityState.securityLevel === 'locked') {
    return (
      <div className="p-6 bg-red-50 border border-red-200 rounded-lg">
        <div className="flex items-center gap-3 mb-4">
          <Shield className="h-6 w-6 text-red-600" />
          <h3 className="text-lg font-semibold text-red-800">Calculator Temporarily Locked</h3>
        </div>
        <Alert className="border-red-200 bg-red-50">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription className="text-red-700">
            This calculator has been temporarily locked due to multiple security violations. 
            Please refresh the page to reset the security status.
          </AlertDescription>
        </Alert>
        <Button 
          onClick={() => window.location.reload()} 
          className="mt-4 bg-red-600 hover:bg-red-700"
        >
          Refresh Page
        </Button>
      </div>
    );
  }

  // Render rate limited state
  if (securityState.isRateLimited) {
    return (
      <div className="p-6 bg-yellow-50 border border-yellow-200 rounded-lg">
        <div className="flex items-center gap-3 mb-4">
          <Clock className="h-6 w-6 text-yellow-600" />
          <h3 className="text-lg font-semibold text-yellow-800">Rate Limit Reached</h3>
        </div>
        <Alert className="border-yellow-200 bg-yellow-50">
          <Clock className="h-4 w-4" />
          <AlertDescription className="text-yellow-700">
            You've reached the maximum number of calculations for this hour. 
            Please wait a moment before trying again.
          </AlertDescription>
        </Alert>
        <div className="mt-4 text-sm text-yellow-700">
          <p>Remaining requests: {securityState.remainingRequests}</p>
          <p>Calculator: {calculatorName}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="relative">
      {/* Security Level Indicator */}
      {securityState.securityLevel !== 'normal' && (
        <div className="mb-4 p-3 bg-orange-50 border border-orange-200 rounded-lg">
          <div className="flex items-center gap-2">
            <AlertTriangle className="h-4 w-4 text-orange-600" />
            <span className="text-sm font-medium text-orange-800">
              Elevated Security Mode
            </span>
          </div>
          <p className="text-sm text-orange-700 mt-1">
            Input validation has been strengthened due to recent security events.
          </p>
        </div>
      )}

      {/* Security Info Toggle */}
      <div className="absolute top-2 right-2 z-10">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => setShowSecurityInfo(!showSecurityInfo)}
          className="h-8 w-8 p-0"
        >
          <Shield className="h-4 w-4" />
        </Button>
      </div>

      {/* Security Information Panel */}
      {showSecurityInfo && (
        <div className="mb-4 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <h4 className="font-semibold text-blue-800 mb-2">Security Information</h4>
          <div className="text-sm text-blue-700 space-y-1">
            <p>Calculator: {calculatorName}</p>
            <p>Remaining calculations: {securityState.remainingRequests}</p>
            <p>Security level: {securityState.securityLevel}</p>
            <p>Last calculation: {securityState.lastCalculation?.toLocaleTimeString() || 'None'}</p>
            <p>All inputs are sanitized and validated for security</p>
            <p>Financial data is encrypted and stored securely</p>
          </div>
        </div>
      )}

      {/* Calculator Content with Security Context */}
      <div className="calculator-content">
        {React.cloneElement(children as React.ReactElement, { 
          securityContext,
          onSecurityEvent: handleSecurityViolation
        })}
      </div>

      {/* Security Footer */}
      <div className="mt-6 pt-4 border-t border-gray-200">
        <div className="flex items-center justify-between text-xs text-gray-500">
          <div className="flex items-center gap-2">
            <Shield className="h-3 w-3" />
            <span>Secured by Vueni Financial Security</span>
          </div>
          <div className="flex items-center gap-4">
            <span>Calculations: {100 - securityState.remainingRequests}/100</span>
            <span>Security: {securityState.securityLevel}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Hook for secure calculator input handling
 */
export function useSecureCalculator(calculatorName: string) {
  const [securityState, setSecurityState] = useState({
    isRateLimited: false,
    remainingRequests: 100
  });

  const validateAndSanitizeInput = (type: string, value: string | number) => {
    // Check rate limit before processing
    if (security.rateLimit.isRateLimited(`calculator:${calculatorName}`)) {
      setSecurityState(prev => ({ ...prev, isRateLimited: true }));
      throw new Error('Rate limit exceeded');
    }

    try {
      switch (type) {
        case 'amount':
          return security.sanitize.sanitizeFinancialAmount(value);
        case 'percentage':
          return security.sanitize.sanitizePercentage(value);
        case 'interestRate':
          return security.sanitize.sanitizeInterestRate(value);
        case 'year':
          return security.sanitize.sanitizeYear(value);
        case 'timePeriod':
          return security.sanitize.sanitizeTimePeriod(value);
        case 'text':
          return security.sanitize.sanitizeText(value);
        default:
          return value;
      }
    } catch (error) {
      VueniSecurityMonitoring.logEvent(
        'INVALID_INPUT',
        'medium',
        `Invalid input in ${calculatorName}: ${error.message}`,
        { calculatorName, type, value }
      );
      throw error;
    }
  };

  const performSecureCalculation = async (calculationFn: () => any) => {
    try {
      // Update remaining requests
      const remaining = security.rateLimit.getRemainingRequests(`calculator:${calculatorName}`);
      setSecurityState(prev => ({ ...prev, remainingRequests: remaining }));

      const result = await calculationFn();

      VueniSecurityMonitoring.logEvent(
        'FINANCIAL_CALCULATION_SUCCESS',
        'low',
        `Successful calculation in ${calculatorName}`,
        { calculatorName }
      );

      return result;
    } catch (error) {
      VueniSecurityMonitoring.logEvent(
        'FINANCIAL_CALCULATION_ERROR',
        'medium',
        `Calculation error in ${calculatorName}: ${error.message}`,
        { calculatorName, error: error.message }
      );
      throw error;
    }
  };

  return {
    validateAndSanitizeInput,
    performSecureCalculation,
    securityState,
    isRateLimited: securityState.isRateLimited,
    remainingRequests: securityState.remainingRequests
  };
}

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SimpleGlassCard from '@/shared/ui/SimpleGlassCard';
import { vueniTheme } from '@/theme/unified';
import { 
  CreditCard, 
  TrendingUp, 
  TrendingDown,
  ArrowRight,
  Info,
  Bar,
  Tooltip,
} from 'lucide-react';
import { creditScoreService } from '@/features/credit/api/creditScoreService';
import { getScoreColor, getScoreGrade } from '@/shared/utils/formatters';
import { 
  AreaChart,
  Area,
  ResponsiveContainer,
} from 'recharts';

interface CreditScoreData {
  score: number;
  maxScore: number;
  change: {
    amount: number;
    period: string;
  };
  utilization: number;
  rating: string;
  nextReviewDate: string;
}

interface CleanCreditScoreCardProps {
  creditData?: CreditScoreData;
  onViewReport?: () => void;
  className?: string;
}

const CleanCreditScoreCard = ({ 
  creditData,
  onViewReport,
  className 
}: CleanCreditScoreCardProps) => {
  const navigate = useNavigate();
  const [data, setData] = useState<CreditScoreData | null>(creditData ?? null);
  const [animatedScore, setAnimatedScore] = useState(0);
  const [animatedUtilization, setAnimatedUtilization] = useState(0);

  // Fetch data if not provided via props to keep in sync with CreditScorePage
  useEffect(() => {
    if (!data) {
      (async () => {
        try {
          const current = await creditScoreService.getCurrentScore();
          // Map service data to CreditScoreData shape
          setData({
            score: current.score,
            maxScore: 850,
            change: {
              amount: current.score - 750, // Mock change calculation
              period: 'last month'
            },
            utilization: current.factors?.find(f => f.factor === 'Credit Utilization')?.percentage ?? 23,
            rating: current.scoreRange,
            nextReviewDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString()
          });
        } catch (e) {
          console.error('Failed to fetch credit score for card', e);
        }
      })();
    }
  }, [data]);

  useEffect(() => {
    if (!data) return;
    const timer = setTimeout(() => {
      setAnimatedScore(data.score);
      setAnimatedUtilization(data.utilization);
    }, 300);
    return () => clearTimeout(timer);
  }, [data]);

  const getScoreColor = (score: number) => {
    if (score >= 740) return colors.status.success;
    if (score >= 670) return colors.status.warning;
    return colors.status.error;
  };

  const getScoreRating = (score: number) => {
    if (score >= 800) return 'Excellent';
    if (score >= 740) return 'Very Good';
    if (score >= 670) return 'Good';
    if (score >= 580) return 'Fair';
    return 'Poor';
  };

  const renderFactors = (factors: any[]) => {
    if (!factors?.length) return null;
    return (
      <div className="space-y-4">
        {factors.map((factor, index) => {
          const progress = factor.score;
          const statusColor = getScoreColor(progress);

          return (
            <div className="flex items-center justify-between">
              <span className="text-sm text-white/70">{factor.factor}</span>
              <div className="w-16 h-2 bg-white/[0.06] rounded-full">
                <div 
                  className="absolute top-0 left-0 h-full rounded-full"
                  style={{ 
                    width: `${progress}%`,
                    backgroundColor: statusColor,
                    transition: `width ${500 + index * 100}ms ease-out`
                  }}
                />
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const renderHistoryChart = () => {
    if (!history?.length) return null;

    return (
      <ResponsiveContainer width="100%" height={120}>
        <AreaChart 
          data={history}
          margin={{ top: 5, right: 20, left: -10, bottom: 0 }}
        >
          <defs>
            <linearGradient id="scoreGradient" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor={vueniTheme.colors.palette.primary} stopOpacity={0.3}/>
              <stop offset="95%" stopColor={vueniTheme.colors.palette.primary} stopOpacity={0}/>
            </linearGradient>
          </defs>
          <Tooltip 
            contentStyle={{ 
              backgroundColor: 'rgba(26, 26, 36, 0.8)', 
              borderColor: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '0.75rem',
              backdropFilter: 'blur(4px)',
            }}
            labelFormatter={(label) => new Date(label).toLocaleDateString('en-US', { month: 'short', year: 'numeric' })}
            formatter={(value: number) => [value, 'Score']}
          />
          <Area 
            type="monotone" 
            dataKey="score" 
            stroke={vueniTheme.colors.palette.primary}
            strokeWidth={2} 
            fill="url(#scoreGradient)" 
            dot={false}
            activeDot={{ r: 6, strokeWidth: 2, stroke: vueniTheme.colors.text.primary, fill: vueniTheme.colors.palette.primary }}
          />
        </AreaChart>
      </ResponsiveContainer>
    );
  };

  if (!data) {
    return null; // or skeleton state (omitted for brevity)
  }

  const scorePercentage = (animatedScore / data.maxScore) * 100;
  const circumference = 2 * Math.PI * 45; // radius of 45
  const strokeDashoffset = circumference - (scorePercentage / 100) * circumference;

  return (
    <SimpleGlassCard className={`p-6 ${className || ''}`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-xl bg-white/[0.06]">
            <CreditCard className="w-5 h-5 text-blue-400" />
          </div>
          <div>
            <h3 className="font-semibold text-white">Credit Score</h3>
            <p className="text-white/60 text-sm">Updated weekly</p>
          </div>
        </div>

        <button className="p-2 rounded-xl bg-white/[0.06] hover:bg-white/[0.08] transition-colors">
          <Info className="w-4 h-4 text-white/70" />
        </button>
      </div>

      {/* Credit Score Circle */}
      <div className="flex items-center justify-center mb-6">
        <div className="relative w-32 h-32">
          <svg className="w-32 h-32 transform -rotate-90" viewBox="0 0 100 100">
            {/* Background circle */}
            <circle
              cx="50"
              cy="50"
              r="45"
              fill="none"
              stroke="rgba(255,255,255,0.1)"
              strokeWidth="6"
            />
            {/* Progress circle */}
            <circle
              cx="50"
              cy="50"
              r="45"
              fill="none"
              stroke={getScoreColor(data.score)}
              strokeWidth="6"
              strokeLinecap="round"
              strokeDasharray={circumference}
              strokeDashoffset={strokeDashoffset}
              className="transition-all duration-1000 ease-out"
            />
          </svg>

          {/* Score display */}
          <div className="absolute inset-0 flex flex-col items-center justify-center">
            <div 
              className="text-3xl font-bold"
              style={{ color: getScoreColor(data.score) }}
            >
              {animatedScore}
            </div>
            <div className="text-xs text-white/60">
              out of {data.maxScore}
            </div>
          </div>
        </div>
      </div>

      {/* Score Rating */}
      <div className="text-center mb-6">
        <div 
          className="text-lg font-semibold mb-1"
          style={{ color: getScoreColor(data.score) }}
        >
          {getScoreRating(data.score)}
        </div>
        <div className="flex items-center justify-center gap-2">
          <div 
            className="flex items-center gap-1"
            style={{ 
              color: data.change.amount >= 0 ? colors.status.success : colors.status.error 
            }}
          >
            {data.change.amount >= 0 ? (
              <TrendingUp className="w-4 h-4" />
            ) : (
              <TrendingDown className="w-4 h-4" />
            )}
            <span className="text-sm font-medium">
              {data.change.amount >= 0 ? '+' : ''}{data.change.amount}
            </span>
          </div>
          <span className="text-white/60 text-sm">
            {data.change.period}
          </span>
        </div>
      </div>

      {/* Credit Utilization */}
      <div className="mb-6">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm text-white/70">Credit Utilization</span>
          <span className="text-sm font-medium text-white">
            {animatedUtilization}%
          </span>
        </div>

        <div className="w-full bg-white/[0.06] rounded-full h-2">
          <div 
            className="h-2 rounded-full transition-all duration-1000 ease-out"
            style={{ 
              width: `${animatedUtilization}%`,
              backgroundColor: animatedUtilization <= 30 
                ? colors.status.success 
                : animatedUtilization <= 50 
                ? colors.status.warning 
                : colors.status.error
            }}
          />
        </div>

        <p className="text-xs text-white/50 mt-1">
          {animatedUtilization <= 30 ? 'Excellent' : animatedUtilization <= 50 ? 'Good' : 'High'} utilization
        </p>
      </div>

      {/* Action Button */}
      <button 
        onClick={() => {
          if (onViewReport) {
            onViewReport();
          } else {
            // Fallback: navigate to credit tab on dashboard
            navigate('/?tab=credit');
          }
        }}
        className="w-full flex items-center justify-between p-3 rounded-2xl bg-white/[0.06] hover:bg-white/[0.08] transition-all duration-200 group"
      >
        <span className="text-white font-medium">View Full Report</span>
        <ArrowRight className="w-4 h-4 text-white/70 group-hover:translate-x-1 transition-transform" />
      </button>

      {/* Next Review */}
      <div className="mt-4 pt-4 border-t border-white/[0.08]">
        <p className="text-xs text-white/50 text-center">
          Next review: {new Date(data.nextReviewDate).toLocaleDateString()}
        </p>
      </div>

      <div className="space-y-6">
        {renderFactors(creditFactors.breakdown)}
        {renderHistoryChart()}
      </div>
    </SimpleGlassCard>
  );
};

export default CleanCreditScoreCard;

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { TrendingUp, TrendingDown, AlertCircle, CheckCircle } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import AnimatedCircularProgress from '../insights/components/AnimatedCircularProgress';
import { creditScoreService } from '@/features/credit/api/creditScoreService';
import { CreditScore } from '@/types/creditScore';
import { UniversalCard } from '@/shared/ui/UniversalCard';
import { OptimizedScoreCard } from '@/features/insights/components/components/OptimizedScoreCard';

const CreditScoreCard = React.memo(() => {
  const navigate = useNavigate();
  const [creditScore, setCreditScore] = useState<CreditScore | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadCreditScore = async () => {
      try {
        const score = await creditScoreService.getCurrentScore();
        setCreditScore(score);
      } catch (error) {
        console.error('Failed to load credit score:', error);
      } finally {
        setLoading(false);
      }
    };

    loadCreditScore();
  }, []);

  const getScoreColor = useMemo(() => (score: number) => {
    if (score >= 800) return '#22c55e'; // Excellent
    if (score >= 740) return '#84cc16'; // Very Good  
    if (score >= 670) return '#eab308'; // Good
    if (score >= 580) return '#f97316'; // Fair
    return '#ef4444'; // Poor
  }, []);

  const getScoreDescription = useMemo(() => {
    const descriptions = {
      'Excellent': 'You have excellent credit! Keep up the great work.',
      'Very Good': 'Your credit is in great shape with room for small improvements.',
      'Good': 'You have good credit with opportunities to improve.',
      'Fair': 'Your credit is fair. Focus on improvement strategies.',
      'Poor': 'Work on building your credit with consistent payments.'
    };
    return (range: string) => descriptions[range as keyof typeof descriptions] || '';
  }, []);

  const handleViewFullReport = useCallback(() => {
    navigate('/credit-score');
  }, [navigate]);

  if (loading) {
    return (
      <div className="liquid-glass-card p-6 animate-pulse">
        <div className="h-8 bg-slate-700 rounded mb-4"></div>
        <div className="h-32 bg-slate-700 rounded mb-4"></div>
        <div className="h-4 bg-slate-700 rounded"></div>
      </div>
    );
  }

  if (!creditScore) {
    return (
      <div className="liquid-glass-card p-6">
        <div className="text-center">
          <AlertCircle className="w-12 h-12 text-orange-400 mx-auto mb-4" />
          <h3 className="text-white text-lg font-semibold mb-2">Credit Score Unavailable</h3>
          <p className="text-slate-400 text-sm">We're working to get your credit score. Check back soon!</p>
        </div>
      </div>
    );
  }

  return (
    <UniversalCard variant="glass" className="p-6" interactive>
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-2xl font-black text-white tracking-wide">Credit Score</h3>
        <div className="text-xs text-slate-400 font-medium italic">
          Updated {new Date(creditScore.lastUpdated).toLocaleDateString()}
        </div>
      </div>

      {/* Optimized Score Display */}
      <div className="mb-6">
        <OptimizedScoreCard
          data={{
            score: creditScore.score,
            maxScore: 850,
            label: creditScore.scoreRange,
            description: getScoreDescription(creditScore.scoreRange),
            color: getScoreColor(creditScore.score),
            trend: {
              direction: 'up',
              percentage: 5
            }
          }}
          variant="enhanced"
          size="lg"
        />
      </div>

      {/* Quick Factors */}
      <div className="space-y-3 mb-6">
        <h4 className="text-white font-black text-sm tracking-wide">Key Factors</h4>
        {creditScore.factors.slice(0, 3).map((factor, index) => (
          <div key={index} className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {factor.status === 'Positive' ? (
                <CheckCircle className="w-4 h-4 text-green-400" />
              ) : factor.status === 'Negative' ? (
                <AlertCircle className="w-4 h-4 text-orange-400" />
              ) : (
                <div className="w-4 h-4 rounded-full bg-slate-500" />
              )}
              <span className="text-slate-300 text-sm">{factor.factor}</span>
            </div>
            <span className="text-xs text-slate-400">{factor.percentage}%</span>
          </div>
        ))}
      </div>

      {/* Optimized Action Button */}
      <UniversalCard
        variant="glass"
        className="cursor-pointer hover:scale-105 transition-transform"
        interactive
        onClick={handleViewFullReport}
      >
        <div className="py-3 text-center">
          <span className="text-white font-bold text-lg tracking-wide">
            View Full Credit Report
          </span>
        </div>
      </UniversalCard>
    </UniversalCard>
  );
});

