    switch (sortBy) {
      case 'priority':
        return a.priority - b.priority;
      case 'progress':
        return b.progress.percentComplete - a.progress.percentComplete;
      case 'deadline':
        return a.targetDate.getTime() - b.targetDate.getTime();
      case 'amount':
        return b.targetAmount - a.targetAmount;
      default:
