import React, { useState, useEffect } from 'react';
import {
  Briefcase,
  Calendar,
  DollarSign,
  TrendingUp,
  Target,
  AlertCircle,
  CheckCircle,
  Calculator,
  PieChart,
  Clock,
  Zap
} from 'lucide-react';
import { RetirementPlan, RiskProfile } from '@/types/financialPlanning';
import { financialPlanningService } from '@/features/planning/api/financialPlanningService';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart as RechartsPieChart, Cell, Pie } from 'recharts';
import { cn } from '@/shared/lib/utils';

interface RetirementPlannerProps {
  familyId: string;
}

const RetirementPlanner = ({ familyId }: RetirementPlannerProps) => {
  const [plan, setPlan] = useState<RetirementPlan | null>(null);
  const [inputs, setInputs] = useState({
    currentAge: 35,
    retirementAge: 65,
    currentIncome: 100000,
    currentSavings: 150000,
    monthlyContribution: 2000,
    riskProfile: 'moderate' as RiskProfile
  });
  const [loading, setLoading] = useState(false);
  const [showProjections, setShowProjections] = useState(false);

  useEffect(() => {
    generatePlan();
  }, []);

  const generatePlan = async () => {
    setLoading(true);
    try {
      const newPlan = await financialPlanningService.createRetirementPlan(
        familyId,
        inputs.currentAge,
        inputs.retirementAge,
        inputs.currentIncome,
        inputs.currentSavings,
        inputs.monthlyContribution,
        inputs.riskProfile
      );
      setPlan(newPlan);
    } catch (error) {
      console.error('Failed to generate retirement plan:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field: string, value: any) => {
    setInputs(prev => ({ ...prev, [field]: value }));
  };

  const recalculatePlan = async () => {
    await generatePlan();
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${(value * 100).toFixed(1)}%`;
  };

  // Generate projection data for charts
  const generateProjectionData = () => {
    if (!plan) return [];

    const data = [];
    const yearsToRetirement = plan.yearsToRetirement;

    for (let year = 0; year <= yearsToRetirement; year++) {
      const currentValue = plan.currentSavings * Math.pow(1 + plan.projections.expectedReturn, year);
      const contributionValue = (plan.monthlyContribution * 12) * 
        ((Math.pow(1 + plan.projections.expectedReturn, year) - 1) / plan.projections.expectedReturn);

      data.push({
        year: inputs.currentAge + year,
        savings: currentValue + contributionValue,
        target: plan.projections.targetRetirementSavings * (year / yearsToRetirement)
      });
    }

    return data;
  };

  const riskAllocationData = [
    { name: 'Stocks', value: inputs.riskProfile === 'conservative' ? 40 : inputs.riskProfile === 'moderate' ? 70 : 85, color: '#3b82f6' },
    { name: 'Bonds', value: inputs.riskProfile === 'conservative' ? 50 : inputs.riskProfile === 'moderate' ? 25 : 10, color: '#10b981' },
    { name: 'Other', value: inputs.riskProfile === 'conservative' ? 10 : inputs.riskProfile === 'moderate' ? 5 : 5, color: '#f59e0b' }
  ];

  if (loading && !plan) {
    return (
      <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
        <div className="flex items-center justify-center py-12">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
            <p className="text-white/70">Calculating your retirement plan...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white flex items-center gap-3">
            <Briefcase className="w-7 h-7 text-blue-400" />
            Retirement Planner
          </h2>
          <p className="text-white/60 mt-1">
            Plan for a secure financial future
          </p>
        </div>

        <button
          onClick={() => setShowProjections(!showProjections)}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-xl transition-colors flex items-center gap-2"
        >
          <PieChart className="w-4 h-4" />
          {showProjections ? 'Hide' : 'Show'} Projections
        </button>
      </div>

      {/* Input Panel */}
      <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
        <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
          <Calculator className="w-5 h-5 text-blue-400" />
          Your Information
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Current Age
            </label>
            <input
              type="number"
              value={inputs.currentAge}
              onChange={(e) => handleInputChange('currentAge', parseInt(e.target.value))}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Retirement Age
            </label>
            <input
              type="number"
              value={inputs.retirementAge}
              onChange={(e) => handleInputChange('retirementAge', parseInt(e.target.value))}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Current Annual Income
            </label>
            <input
              type="number"
              value={inputs.currentIncome}
              onChange={(e) => handleInputChange('currentIncome', parseInt(e.target.value))}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Current Retirement Savings
            </label>
            <input
              type="number"
              value={inputs.currentSavings}
              onChange={(e) => handleInputChange('currentSavings', parseInt(e.target.value))}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Monthly Contribution
            </label>
            <input
              type="number"
              value={inputs.monthlyContribution}
              onChange={(e) => handleInputChange('monthlyContribution', parseInt(e.target.value))}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              Risk Profile
            </label>
            <select
              value={inputs.riskProfile}
              onChange={(e) => handleInputChange('riskProfile', e.target.value)}
              className="w-full bg-white/[0.05] border border-white/[0.08] rounded-lg text-white px-3 py-2"
            >
              <option value="conservative">Conservative (4% return)</option>
              <option value="moderate">Moderate (7% return)</option>
              <option value="aggressive">Aggressive (10% return)</option>
            </select>
          </div>
        </div>

        <button
          onClick={recalculatePlan}
          disabled={loading}
          className="mt-4 bg-blue-500 hover:bg-blue-600 disabled:opacity-50 text-white px-6 py-2 rounded-lg transition-colors flex items-center gap-2"
        >
          {loading ? (
            <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
          ) : (
            <Calculator className="w-4 h-4" />
          )}
          Recalculate Plan
        </button>
      </div>

      {/* Results */}
      {plan && (
        <>
          {/* Key Metrics */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-white/60 text-sm">Years to Retirement</p>
                  <p className="text-3xl font-bold text-white">{plan.yearsToRetirement}</p>
                </div>
                <Calendar className="w-8 h-8 text-blue-400" />
              </div>
            </div>

            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-white/60 text-sm">Projected Value</p>
                  <p className="text-3xl font-bold text-white">
                    {formatCurrency(plan.projections.totalRetirementValue)}
                  </p>
                </div>
                <TrendingUp className="w-8 h-8 text-green-400" />
              </div>
            </div>

            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-white/60 text-sm">Monthly Income</p>
                  <p className="text-3xl font-bold text-white">
                    {formatCurrency(plan.projections.monthlyIncomeAtRetirement)}
                  </p>
                </div>
                <DollarSign className="w-8 h-8 text-purple-400" />
              </div>
            </div>

            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-white/60 text-sm">
                    {plan.projections.shortfall > 0 ? 'Shortfall' : 'Surplus'}
                  </p>
                  <p className={cn(
                    "text-3xl font-bold",
                    plan.projections.shortfall > 0 ? "text-red-400" : "text-green-400"
                  )}>
                    {plan.projections.shortfall > 0 ? '-' : '+'}
                    {formatCurrency(Math.abs(plan.projections.shortfall))}
                  </p>
                </div>
                {plan.projections.shortfall > 0 ? (
                  <AlertCircle className="w-8 h-8 text-red-400" />
                ) : (
                  <CheckCircle className="w-8 h-8 text-green-400" />
                )}
              </div>
            </div>
          </div>

          {/* Analysis */}
          <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
            <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
              <Target className="w-5 h-5 text-blue-400" />
              Retirement Analysis
            </h3>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div className="space-y-4">
                <div className="flex items-center justify-between p-4 bg-white/[0.03] rounded-xl">
                  <span className="text-white/80">Target Retirement Savings</span>
                  <span className="font-semibold text-white">
                    {formatCurrency(plan.projections.targetRetirementSavings)}
                  </span>
                </div>

                <div className="flex items-center justify-between p-4 bg-white/[0.03] rounded-xl">
                  <span className="text-white/80">Projected Retirement Value</span>
                  <span className="font-semibold text-white">
                    {formatCurrency(plan.projections.totalRetirementValue)}
                  </span>
                </div>

                <div className="flex items-center justify-between p-4 bg-white/[0.03] rounded-xl">
                  <span className="text-white/80">Expected Annual Return</span>
                  <span className="font-semibold text-white">
                    {formatPercentage(plan.projections.expectedReturn)}
                  </span>
                </div>

                <div className="flex items-center justify-between p-4 bg-white/[0.03] rounded-xl">
                  <span className="text-white/80">Income Replacement Ratio</span>
                  <span className="font-semibold text-white">
                    {Math.round((plan.projections.monthlyIncomeAtRetirement * 12) / plan.currentIncome * 100)}%
                  </span>
                </div>
              </div>

              {/* Asset Allocation */}
              <div>
                <h4 className="text-md font-semibold text-white mb-3">Recommended Asset Allocation</h4>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <RechartsPieChart>
                      <Pie
                        data={riskAllocationData}
                        cx="50%"
                        cy="50%"
                        innerRadius={40}
                        outerRadius={80}
                        paddingAngle={5}
                        dataKey="value"
                      >
                        {riskAllocationData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                      </Pie>
                      <Tooltip 
                        formatter={(value) => [`${value}%`, 'Allocation']}
                        contentStyle={{
                          backgroundColor: 'rgba(0, 0, 0, 0.8)',
                          border: '1px solid rgba(255, 255, 255, 0.1)',
                          borderRadius: '8px',
                          color: 'white'
                        }}
                      />
                    </RechartsPieChart>
                  </ResponsiveContainer>
                </div>

                <div className="space-y-2 mt-4">
                  {riskAllocationData.map((item, index) => (
                    <div key={index} className="flex items-center justify-between text-sm">
                      <div className="flex items-center gap-2">
                        <div 
                          className="w-3 h-3 rounded"
                          style={{ backgroundColor: item.color }}
                        />
                        <span className="text-white/80">{item.name}</span>
                      </div>
                      <span className="text-white">{item.value}%</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Projections Chart */}
          {showProjections && (
            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <h3 className="text-lg font-semibold text-white mb-6 flex items-center gap-2">
                <TrendingUp className="w-5 h-5 text-blue-400" />
                Savings Growth Projection
              </h3>

              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <LineChart data={generateProjectionData()}>
                    <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                    <XAxis 
                      dataKey="year" 
                      stroke="rgba(255,255,255,0.6)"
                      tick={{ fill: 'rgba(255,255,255,0.6)' }}
                    />
                    <YAxis 
                      stroke="rgba(255,255,255,0.6)"
                      tick={{ fill: 'rgba(255,255,255,0.6)' }}
                      tickFormatter={(value) => `$${(value / 1000000).toFixed(1)}M`}
                    />
                    <Tooltip 
                      formatter={(value, name) => [
                        formatCurrency(value as number), 
                        name === 'savings' ? 'Projected Savings' : 'Target Savings'
                      ]}
                      labelFormatter={(label) => `Age ${label}`}
                      contentStyle={{
                        backgroundColor: 'rgba(0, 0, 0, 0.8)',
                        border: '1px solid rgba(255, 255, 255, 0.1)',
                        borderRadius: '8px',
                        color: 'white'
                      }}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="savings" 
                      stroke="#3b82f6" 
                      strokeWidth={3}
                      dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="target" 
                      stroke="#ef4444" 
                      strokeWidth={2}
                      strokeDasharray="5 5"
                      dot={{ fill: '#ef4444', strokeWidth: 2, r: 3 }}
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>

              <div className="flex items-center justify-center gap-6 mt-4 text-sm">
                <div className="flex items-center gap-2">
                  <div className="w-4 h-0.5 bg-blue-500"></div>
                  <span className="text-white/80">Projected Savings</span>
                </div>
                <div className="flex items-center gap-2">
                  <div className="w-4 h-0.5 bg-red-500 border-dashed"></div>
                  <span className="text-white/80">Target Savings</span>
                </div>
              </div>
            </div>
          )}

          {/* Recommendations */}
          {plan.recommendations.length > 0 && (
            <div className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6">
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                <Zap className="w-5 h-5 text-yellow-400" />
                Recommendations
              </h3>

              <div className="space-y-4">
                {plan.recommendations.map((rec, index) => (
                  <div key={index} className="bg-white/[0.03] rounded-xl p-4 border border-white/[0.05]">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h4 className="font-semibold text-white mb-2">{rec.title}</h4>
                        <p className="text-white/70 text-sm mb-3">{rec.description}</p>

                        <div className="space-y-2">
                          {rec.actionItems.map((item, itemIndex) => (
                            <div key={itemIndex} className="flex items-center gap-2 text-sm">
                              <div className="w-1.5 h-1.5 bg-blue-400 rounded-full"></div>
                              <span className="text-white/80">{item}</span>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div className="text-right">
                        <span className={cn(
                          "text-xs px-2 py-1 rounded-lg font-medium",
                          rec.impact === 'high' ? "bg-red-500/20 text-red-400" :
                          rec.impact === 'medium' ? "bg-yellow-500/20 text-yellow-400" :
                          "bg-green-500/20 text-green-400"
                        )}>
                          {rec.impact} impact
                        </span>
                        {rec.estimatedBenefit > 0 && (
                          <p className="text-green-400 text-sm mt-1">
                            Save {formatCurrency(rec.estimatedBenefit)}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default RetirementPlanner;

import React, { useState, useEffect } from 'react';
import {
  DollarSign,
  Target,
  TrendingDown,
  Calendar,
  Zap,
  AlertCircle,
  CheckCircle,
  ArrowRight,
  CreditCard
} from 'lucide-react';
import { DebtPayoffPlan } from '@/shared/types/financialPlanning';
import { MockFinancialPlanningAPI } from '@/mocks/financialPlanningMocks';
import PlanningCard from '../shared/PlanningCard';
import { cn } from '@/shared/lib/utils';

interface DebtPayoffTabProps {
  familyId: string;
}

const DebtPayoffTab: React.FC<DebtPayoffTabProps> = ({ familyId }) => {
  const [plan, setPlan] = useState<DebtPayoffPlan | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDebtPlan();
  }, [familyId]);

  const loadDebtPlan = async () => {
    setLoading(true);
    try {
      const debtPlan = await MockFinancialPlanningAPI.getDebtPayoffPlan(familyId);
      setPlan(debtPlan);
    } catch (error) {
      console.error('Failed to load debt plan:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${(value * 100).toFixed(1)}%`;
  };

  const getDebtColor = (interestRate: number) => {
    if (interestRate >= 0.20) return 'text-red-400';
    if (interestRate >= 0.10) return 'text-orange-400';
    return 'text-yellow-400';
  };

  if (loading) {
    return (
      <div className="space-y-6">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 animate-pulse">
            <div className="flex items-center gap-4 mb-4">
              <div className="w-8 h-8 bg-white/[0.05] rounded"></div>
              <div className="h-6 bg-white/[0.05] rounded w-48"></div>
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-white/[0.05] rounded w-full"></div>
              <div className="h-4 bg-white/[0.05] rounded w-3/4"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (!plan) {
    return (
      <div className="text-center py-12">
        <AlertCircle className="w-12 h-12 text-orange-400 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-white mb-2">No Debt Plan Found</h3>
        <p className="text-white/60">Create a debt payoff strategy to get started.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white flex items-center gap-3">
            <DollarSign className="w-7 h-7 text-red-400" />
            Debt Payoff Strategy
          </h2>
          <p className="text-white/60 mt-1">
            {plan.strategy === 'avalanche' ? 'Avalanche Method' : 'Snowball Method'} â€¢ 
            {plan.projections.monthsToPayoff} months to debt-free
          </p>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <PlanningCard
          title="Total Debt"
          icon={CreditCard}
          iconColor="text-red-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.projections.totalDebt)}
          </div>
          <div className="text-white/60 text-sm mt-1">
            {plan.debts.length} accounts
          </div>
        </PlanningCard>

        <PlanningCard
          title="Extra Payment"
          icon={Target}
          iconColor="text-blue-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.extraPayment)}
          </div>
          <div className="text-white/60 text-sm mt-1">
            Per month
          </div>
        </PlanningCard>

        <PlanningCard
          title="Total Interest"
          icon={TrendingDown}
          iconColor="text-orange-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.projections.totalInterest)}
          </div>
          <div className="text-white/60 text-sm mt-1">
            Over {plan.projections.monthsToPayoff} months
          </div>
        </PlanningCard>

        <PlanningCard
          title="Freedom Date"
          icon={Calendar}
          iconColor="text-green-400"
        >
          <div className="text-lg font-bold text-white">
            {plan.projections.payoffDate.toLocaleDateString('en-US', { 
              month: 'short', 
              year: 'numeric' 
            })}
          </div>
          <div className="text-white/60 text-sm mt-1">
            Debt-free in {plan.projections.monthsToPayoff} months
          </div>
        </PlanningCard>
      </div>

      {/* Strategy Information */}
      <PlanningCard
        variant="gradient"
        icon={Target}
        iconColor="text-blue-400"
        title={`${plan.strategy === 'avalanche' ? 'Avalanche' : 'Snowball'} Strategy Active`}
        description={
          plan.strategy === 'avalanche' 
            ? 'Paying minimums on all debts, extra payments go to highest interest rate first.'
            : 'Paying minimums on all debts, extra payments go to smallest balance first.'
        }
      >
        <div className="mt-4 p-4 bg-white/[0.05] rounded-xl">
          <div className="flex items-center justify-between">
            <span className="text-white/80">Monthly Savings After Payoff</span>
            <span className="text-2xl font-bold text-green-400">
              {formatCurrency(plan.projections.monthlySavingsAfterPayoff)}
            </span>
          </div>
        </div>
      </PlanningCard>

      {/* Debt List */}
      <PlanningCard
        title="Debt Accounts"
        icon={CreditCard}
        iconColor="text-red-400"
      >
        <div className="space-y-4">
          {plan.payoffSchedule.map((debt, index) => (
            <div key={index} className="p-4 bg-white/[0.03] rounded-xl border border-white/[0.05]">
              <div className="flex items-center justify-between mb-3">
                <div className="flex-1">
                  <h4 className="font-semibold text-white">{debt.name}</h4>
                  <div className="flex items-center gap-4 mt-1">
                    <span className="text-white/60 text-sm">
                      {formatPercentage(debt.interestRate)} APR
                    </span>
                    <span className={cn("text-sm font-medium", getDebtColor(debt.interestRate))}>
                      {debt.interestRate >= 0.20 ? 'High Interest' : 
                       debt.interestRate >= 0.10 ? 'Medium Interest' : 'Low Interest'}
                    </span>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-xl font-bold text-white">
                    {formatCurrency(debt.balance)}
                  </div>
                  <div className="text-white/60 text-sm">
                    {debt.monthsToPayoff} months
                  </div>
                </div>
              </div>

              {/* Progress Bar */}
              <div className="mb-3">
                <div className="flex items-center justify-between text-sm mb-1">
                  <span className="text-white/80">
                    Min: {formatCurrency(debt.minimumPayment)}
                  </span>
                  <span className="text-white/60">
                    Interest: {formatCurrency(debt.totalInterest)}
                  </span>
                </div>
                <div className="w-full bg-white/[0.05] rounded-full h-2">
                  <div 
                    className={cn(
                      "h-2 rounded-full",
                      debt.interestRate >= 0.20 ? "bg-red-400" :
                      debt.interestRate >= 0.10 ? "bg-orange-400" : "bg-yellow-400"
                    )}
                    style={{ width: `${Math.min((debt.totalPaid / (debt.balance + debt.totalInterest)) * 100, 100)}%` }}
                  />
                </div>
              </div>

              <div className="flex items-center justify-between text-sm">
                <span className="text-white/80">
                  Total Paid: {formatCurrency(debt.totalPaid)}
                </span>
                <span className="text-green-400">
                  Save vs. minimum: {formatCurrency(debt.totalInterest * 0.3)}
                </span>
              </div>
            </div>
          ))}
        </div>
      </PlanningCard>

      {/* Recommendations */}
      <PlanningCard
        title="Debt Payoff Recommendations"
        icon={Zap}
        iconColor="text-yellow-400"
      >
        <div className="space-y-4">
          {plan.recommendations.map((rec, index) => (
            <div key={index} className="p-4 bg-white/[0.03] rounded-xl border border-white/[0.05]">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <h4 className="font-semibold text-white mb-2">{rec.title}</h4>
                  <p className="text-white/70 text-sm mb-3">{rec.description}</p>

                  <div className="flex items-center gap-4">
                    <span className={cn(
                      "text-xs px-2 py-1 rounded-lg font-medium",
                      rec.impact === 'high' ? "bg-red-500/20 text-red-400" :
                      rec.impact === 'medium' ? "bg-yellow-500/20 text-yellow-400" :
                      "bg-green-500/20 text-green-400"
                    )}>
                      {rec.impact} impact
                    </span>
                    <span className="text-xs text-green-400">
                      Save: {formatCurrency(rec.estimatedBenefit)}
                    </span>
                  </div>

                  {/* Action Items */}
                  <div className="mt-3">
                    <div className="text-xs text-white/60 mb-2">Recommended Actions:</div>
                    <div className="space-y-1">
                      {rec.actionItems.map((action, actionIndex) => (
                        <div key={actionIndex} className="flex items-center gap-2 text-xs text-white/70">
                          <CheckCircle className="w-3 h-3 text-green-400" />
                          {action}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                <ArrowRight className="w-4 h-4 text-white/60" />
              </div>
            </div>
          ))}
        </div>
      </PlanningCard>
    </div>
  );
};

export default DebtPayoffTab;

import React, { useState, useEffect } from 'react';
import {
  Calendar,
  Baby,
  Home,
  GraduationCap,
  Heart,
  Car,
  Briefcase,
  Plus,
  Target,
  Zap,
  AlertCircle
} from 'lucide-react';
import { LifeEventPlanning } from '@/shared/types/financialPlanning';
import { MockFinancialPlanningAPI } from '@/mocks/financialPlanningMocks';
import PlanningCard from '../shared/PlanningCard';
import { cn } from '@/shared/lib/utils';

interface LifePlanningTabProps {
  familyId: string;
}

const LifePlanningTab: React.FC<LifePlanningTabProps> = ({ familyId }) => {
  const [plans, setPlans] = useState<LifeEventPlanning[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadLifeEventPlans();
  }, [familyId]);

  const loadLifeEventPlans = async () => {
    setLoading(true);
    try {
      const lifeEventPlans = await MockFinancialPlanningAPI.getLifeEventPlans(familyId);
      setPlans(lifeEventPlans);
    } catch (error) {
      console.error('Failed to load life event plans:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const getEventIcon = (eventType: string) => {
    const icons = {
      baby: Baby,
      marriage: Heart,
      house_purchase: Home,
      education: GraduationCap,
      car_purchase: Car,
      job_change: Briefcase,
      retirement: Calendar
    };
    return icons[eventType as keyof typeof icons] || Calendar;
  };

  const getEventColor = (eventType: string) => {
    const colors = {
      baby: 'text-pink-400',
      marriage: 'text-red-400',
      house_purchase: 'text-green-400',
      education: 'text-blue-400',
      car_purchase: 'text-indigo-400',
      job_change: 'text-orange-400',
      retirement: 'text-purple-400'
    };
    return colors[eventType as keyof typeof colors] || 'text-blue-400';
  };

  const getEventLabel = (eventType: string) => {
    const labels = {
      baby: 'Baby/Child',
      marriage: 'Wedding',
      house_purchase: 'Home Purchase',
      education: 'Education',
      car_purchase: 'Vehicle',
      job_change: 'Career Change',
      retirement: 'Retirement'
    };
    return labels[eventType as keyof typeof labels] || eventType;
  };

  const calculateProgress = (plan: LifeEventPlanning) => {
    return Math.min((plan.currentSavings / plan.estimatedCost) * 100, 100);
  };

  const calculateMonthsRemaining = (targetDate: Date) => {
    const now = new Date();
    const diffTime = targetDate.getTime() - now.getTime();
    const diffMonths = Math.ceil(diffTime / (1000 * 60 * 60 * 24 * 30));
    return Math.max(diffMonths, 0);
  };

  if (loading) {
    return (
      <div className="space-y-6">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 animate-pulse">
            <div className="flex items-center gap-4 mb-4">
              <div className="w-8 h-8 bg-white/[0.05] rounded"></div>
              <div className="h-6 bg-white/[0.05] rounded w-48"></div>
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-white/[0.05] rounded w-full"></div>
              <div className="h-4 bg-white/[0.05] rounded w-3/4"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white flex items-center gap-3">
            <Calendar className="w-7 h-7 text-purple-400" />
            Life Event Planning
          </h2>
          <p className="text-white/60 mt-1">
            Plan and save for major life milestones
          </p>
        </div>
        <button className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-xl transition-colors flex items-center gap-2">
          <Plus className="w-4 h-4" />
          Add Event
        </button>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <PlanningCard
          title="Active Plans"
          icon={Target}
          iconColor="text-blue-400"
        >
          <div className="text-2xl font-bold text-white">
            {plans.length}
          </div>
          <div className="text-white/60 text-sm mt-1">
            Life events tracked
          </div>
        </PlanningCard>

        <PlanningCard
          title="Total Estimated Cost"
          icon={Calendar}
          iconColor="text-orange-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plans.reduce((sum, plan) => sum + plan.estimatedCost, 0))}
          </div>
          <div className="text-white/60 text-sm mt-1">
            Across all events
          </div>
        </PlanningCard>

        <PlanningCard
          title="Total Saved"
          icon={Target}
          iconColor="text-green-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plans.reduce((sum, plan) => sum + plan.currentSavings, 0))}
          </div>
          <div className="text-white/60 text-sm mt-1">
            {Math.round(plans.reduce((sum, plan) => sum + calculateProgress(plan), 0) / plans.length || 0)}% complete
          </div>
        </PlanningCard>
      </div>

      {/* Life Event Plans */}
      <div className="space-y-4">
        {plans.map((plan, index) => {
          const EventIcon = getEventIcon(plan.eventType);
          const iconColor = getEventColor(plan.eventType);
          const progress = calculateProgress(plan);
          const monthsRemaining = calculateMonthsRemaining(plan.plannedDate);
          const monthlyNeeded = monthsRemaining > 0 ? 
            (plan.estimatedCost - plan.currentSavings) / monthsRemaining : 0;

          return (
            <PlanningCard
              key={index}
              title={getEventLabel(plan.eventType)}
              icon={EventIcon}
              iconColor={iconColor}
              variant="highlight"
            >
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Progress Section */}
                <div className="lg:col-span-2 space-y-4">
                  {/* Financial Progress */}
                  <div>
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-white/80 text-sm">Financial Progress</span>
                      <span className="text-white/60 text-sm">{Math.round(progress)}%</span>
                    </div>
                    <div className="w-full bg-white/[0.05] rounded-full h-3">
                      <div 
                        className={cn("h-3 rounded-full transition-all duration-500", 
                          progress >= 100 ? "bg-green-400" :
                          progress >= 50 ? "bg-blue-400" : "bg-orange-400"
                        )}
                        style={{ width: `${Math.min(progress, 100)}%` }}
                      />
                    </div>
                  </div>

                  {/* Key Metrics */}
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center p-3 bg-white/[0.03] rounded-xl">
                      <div className="text-lg font-bold text-white">
                        {formatCurrency(plan.estimatedCost)}
                      </div>
                      <div className="text-white/60 text-xs">Target Cost</div>
                    </div>

                    <div className="text-center p-3 bg-white/[0.03] rounded-xl">
                      <div className="text-lg font-bold text-white">
                        {formatCurrency(plan.currentSavings)}
                      </div>
                      <div className="text-white/60 text-xs">Saved</div>
                    </div>

                    <div className="text-center p-3 bg-white/[0.03] rounded-xl">
                      <div className="text-lg font-bold text-white">
                        {formatCurrency(plan.monthlyContribution)}
                      </div>
                      <div className="text-white/60 text-xs">Monthly</div>
                    </div>

                    <div className="text-center p-3 bg-white/[0.03] rounded-xl">
                      <div className="text-lg font-bold text-white">
                        {monthsRemaining}
                      </div>
                      <div className="text-white/60 text-xs">Months Left</div>
                    </div>
                  </div>

                  {/* Timeline */}
                  <div className="p-4 bg-white/[0.03] rounded-xl">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="text-white font-semibold">Target Date</div>
                        <div className="text-white/60 text-sm">
                          {plan.plannedDate.toLocaleDateString('en-US', { 
                            weekday: 'long',
                            year: 'numeric', 
                            month: 'long', 
                            day: 'numeric' 
                          })}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-white font-semibold">
                          {monthlyNeeded > plan.monthlyContribution ? 'Behind Schedule' : 'On Track'}
                        </div>
                        {monthlyNeeded > plan.monthlyContribution && (
                          <div className="text-orange-400 text-sm">
                            Need: {formatCurrency(monthlyNeeded)}/month
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Recommendations Section */}
                <div className="space-y-4">
                  <h4 className="text-white font-semibold flex items-center gap-2">
                    <Zap className="w-4 h-4 text-yellow-400" />
                    Recommendations
                  </h4>

                  {plan.recommendations.map((rec, recIndex) => (
                    <div key={recIndex} className="p-3 bg-white/[0.03] rounded-xl border border-white/[0.05]">
                      <h5 className="font-semibold text-white text-sm mb-1">{rec.title}</h5>
                      <p className="text-white/70 text-xs mb-2">{rec.description}</p>

                      <div className="flex items-center gap-2 mb-2">
                        <span className={cn(
                          "text-xs px-2 py-1 rounded-lg font-medium",
                          rec.impact === 'high' ? "bg-red-500/20 text-red-400" :
                          rec.impact === 'medium' ? "bg-yellow-500/20 text-yellow-400" :
                          "bg-green-500/20 text-green-400"
                        )}>
                          {rec.impact} impact
                        </span>
                        <span className="text-xs text-green-400">
                          +{formatCurrency(rec.estimatedBenefit)}
                        </span>
                      </div>

                      <div className="space-y-1">
                        {rec.actionItems.map((action, actionIndex) => (
                          <div key={actionIndex} className="flex items-center gap-2 text-xs text-white/70">
                            <div className="w-1 h-1 bg-white/40 rounded-full" />
                            {action}
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </PlanningCard>
          );
        })}
      </div>

      {/* No Plans State */}
      {plans.length === 0 && (
        <PlanningCard
          title="No Life Events Planned"
          icon={AlertCircle}
          iconColor="text-orange-400"
          variant="highlight"
        >
          <div className="text-center py-8">
            <Calendar className="w-16 h-16 text-white/20 mx-auto mb-4" />
            <p className="text-white/60 mb-4">
              Start planning for major life events like buying a home, having a baby, or pursuing education.
            </p>
            <button className="bg-purple-500 hover:bg-purple-600 text-white px-6 py-3 rounded-xl transition-colors flex items-center gap-2 mx-auto">
              <Plus className="w-4 h-4" />
              Add Your First Life Event
            </button>
          </div>
        </PlanningCard>
      )}
    </div>
  );
};

