import React, { useState, useEffect } from 'react';
import {
  Briefcase,
  TrendingUp,
  Target,
  Calculator,
  PieChart,
  Zap,
  AlertCircle,
  CheckCircle,
  ArrowRight
} from 'lucide-react';
import { RetirementPlan } from '@/shared/types/financialPlanning';
import { MockFinancialPlanningAPI } from '@/mocks/financialPlanningMocks';
import PlanningCard from '../shared/PlanningCard';
import ProjectionChart from '../shared/ProjectionChart';
import { cn } from '@/shared/lib/utils';

interface RetirementTabProps {
  familyId: string;
}

const RetirementTab: React.FC<RetirementTabProps> = ({ familyId }) => {
  const [plan, setPlan] = useState<RetirementPlan | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadRetirementPlan();
  }, [familyId]);

  const loadRetirementPlan = async () => {
    setLoading(true);
    try {
      const retirementPlan = await MockFinancialPlanningAPI.getRetirementPlan(familyId);
      setPlan(retirementPlan);
    } catch (error) {
      console.error('Failed to load retirement plan:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${(value * 100).toFixed(1)}%`;
  };

  const generateProjectionData = () => {
    if (!plan) return [];

    const data: Array<{
      date: Date;
      value: number;
      label: string;
    }> = [];
    const yearsToRetirement = plan.yearsToRetirement;
    const monthlyContribution = plan.monthlyContribution;
    const expectedReturn = plan.projections.expectedReturn;
    let currentValue = plan.currentSavings;

    for (let year = 0; year <= yearsToRetirement; year++) {
      if (year > 0) {
        currentValue = currentValue * (1 + expectedReturn) + (monthlyContribution * 12);
      }

      data.push({
        date: new Date(Date.now() + year * 365 * 24 * 60 * 60 * 1000),
        value: Math.round(currentValue),
        label: `Year ${year}`
      });
    }

    return data;
  };

  if (loading) {
    return (
      <div className="space-y-6">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="bg-white/[0.02] rounded-2xl border border-white/[0.08] p-6 animate-pulse">
            <div className="flex items-center gap-4 mb-4">
              <div className="w-8 h-8 bg-white/[0.05] rounded"></div>
              <div className="h-6 bg-white/[0.05] rounded w-48"></div>
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-white/[0.05] rounded w-full"></div>
              <div className="h-4 bg-white/[0.05] rounded w-3/4"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (!plan) {
    return (
      <div className="text-center py-12">
        <AlertCircle className="w-12 h-12 text-orange-400 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-white mb-2">No Retirement Plan Found</h3>
        <p className="text-white/60">Create a retirement plan to get started.</p>
      </div>
    );
  }

  const isOnTrack = plan.projections.shortfall <= 0;
  const projectionData = generateProjectionData();

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white flex items-center gap-3">
            <Briefcase className="w-7 h-7 text-blue-400" />
            Retirement Planning
          </h2>
          <p className="text-white/60 mt-1">
            {plan.yearsToRetirement} years to retirement
          </p>
        </div>
      </div>

      {/* Current Status Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <PlanningCard
          title="Current Savings"
          icon={TrendingUp}
          iconColor="text-green-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.currentSavings)}
          </div>
        </PlanningCard>

        <PlanningCard
          title="Monthly Contribution"
          icon={Calculator}
          iconColor="text-blue-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.monthlyContribution)}
          </div>
        </PlanningCard>

        <PlanningCard
          title="Projected Value"
          icon={Target}
          iconColor="text-purple-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.projections.totalRetirementValue)}
          </div>
        </PlanningCard>

        <PlanningCard
          title="Monthly Income"
          icon={PieChart}
          iconColor="text-orange-400"
        >
          <div className="text-2xl font-bold text-white">
            {formatCurrency(plan.projections.monthlyIncomeAtRetirement)}
          </div>
        </PlanningCard>
      </div>

      {/* Status Alert */}
      <PlanningCard
        variant={isOnTrack ? 'gradient' : 'highlight'}
        icon={isOnTrack ? CheckCircle : AlertCircle}
        iconColor={isOnTrack ? 'text-green-400' : 'text-orange-400'}
        title={isOnTrack ? 'On Track for Retirement' : 'Retirement Shortfall Detected'}
        description={
          isOnTrack 
            ? 'Your current savings rate should meet your retirement goals.'
            : `Projected shortfall of ${formatCurrency(plan.projections.shortfall)}.`
        }
      />

      {/* Projection Chart */}
      <ProjectionChart
        data={projectionData}
        title="Retirement Savings Projection"
        type="area"
        height={350}
        color="#3b82f6"
      />

      {/* Recommendations */}
      <PlanningCard
        title="AI Recommendations"
        icon={Zap}
        iconColor="text-yellow-400"
      >
        <div className="space-y-4">
          {plan.recommendations.map((rec, index) => (
            <div key={index} className="p-4 bg-white/[0.03] rounded-xl border border-white/[0.05]">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <h4 className="font-semibold text-white mb-2">{rec.title}</h4>
                  <p className="text-white/70 text-sm mb-3">{rec.description}</p>

                  <div className="flex items-center gap-4">
                    <span className={cn(
                      "text-xs px-2 py-1 rounded-lg font-medium",
                      rec.impact === 'high' ? "bg-red-500/20 text-red-400" :
                      rec.impact === 'medium' ? "bg-yellow-500/20 text-yellow-400" :
                      "bg-green-500/20 text-green-400"
                    )}>
                      {rec.impact} impact
                    </span>
                    <span className="text-xs text-green-400">
                      Benefit: {formatCurrency(rec.estimatedBenefit)}
                    </span>
                  </div>
                </div>

                <ArrowRight className="w-4 h-4 text-white/60" />
              </div>
            </div>
          ))}
        </div>
      </PlanningCard>
    </div>
  );
};

export default RetirementTab;

import React, { useState } from 'react';
import { Calendar, TrendingUp, Plus, Target, Clock } from 'lucide-react';
import { SavingsGoal } from '@/types/savingsGoals';
import { savingsGoalsService } from '@/features/savings/api/savingsGoalsService';
import { UnifiedCard } from '@/shared/ui/UnifiedCard';
import { formatCurrency } from '@/shared/utils/formatters';

interface GoalCardProps {
  goal: SavingsGoal;
  onGoalUpdate?: () => void;
}

const GoalCard = React.memo<GoalCardProps>(({ goal, onGoalUpdate }) => {
  const [showContribution, setShowContribution] = useState(false);
  const [contributionAmount, setContributionAmount] = useState('');
  const [isAddingContribution, setIsAddingContribution] = useState(false);

  const progress = (goal.currentAmount / goal.targetAmount) * 100;
  const daysLeft = Math.ceil((new Date(goal.targetDate).getTime() - Date.now()) / (1000 * 60 * 60 * 24));
  const monthlyNeeded = savingsGoalsService.calculateMonthlyContribution(
    goal.targetAmount, 
    goal.currentAmount, 
    goal.targetDate
  );

  const handleAddContribution = async () => {
    const amount = parseFloat(contributionAmount);
    if (amount > 0) {
      setIsAddingContribution(true);
      try {
        await savingsGoalsService.addContribution(goal.id, {
          amount,
          date: new Date().toISOString(),
          type: 'manual',
          description: 'Manual contribution'
        });
        setContributionAmount('');
        setShowContribution(false);
        onGoalUpdate?.();
      } catch (error) {
        console.error('Failed to add contribution:', error);
      } finally {
        setIsAddingContribution(false);
      }
    }
  };

  const getProgressColor = () => {
    if (progress >= 100) return '#22c55e';
    if (progress >= 75) return '#84cc16';
    if (progress >= 50) return '#eab308';
    if (progress >= 25) return '#f97316';
    return '#ef4444';
  };

  return (
    <UnifiedCard
      title={goal.name}
      subtitle={goal.description || goal.category}
      metric={formatCurrency(goal.currentAmount)}
      delta={{
        value: goal.targetAmount - goal.currentAmount,
        format: 'currency',
        label: 'remaining'
      }}
      icon={goal.icon}
      progress={{
        value: goal.currentAmount,
        max: goal.targetAmount,
        color: getProgressColor(),
        showLabel: true
      }}
      badge={goal.isCompleted ? {
        text: 'Complete',
        variant: 'success'
      } : daysLeft <= 0 ? {
        text: 'Overdue',
        variant: 'error'
      } : daysLeft <= 30 ? {
        text: `${daysLeft} days left`,
        variant: 'warning'
      } : undefined}
      variant="default"
      size="lg"
      interactive={true}
      className="relative overflow-hidden hover:bg-white/[0.03] transition-all"
    >
      {/* Goal Stats */}
      <div className="grid grid-cols-2 gap-4 mt-4 mb-4">
        <div className="text-center">
          <div className="flex items-center justify-center space-x-1 mb-1">
            <Target className="w-3 h-3 text-white/60" />
            <div className="text-white font-semibold text-sm">
              {formatCurrency(monthlyNeeded)}
            </div>
          </div>
          <div className="text-white/60 text-xs">Monthly needed</div>
        </div>
        <div className="text-center">
          <div className="flex items-center justify-center space-x-1 mb-1">
            <Calendar className="w-3 h-3 text-white/60" />
            <div className="text-white font-semibold text-sm">
              {new Date(goal.targetDate).toLocaleDateString('en-US', { 
                month: 'short', 
                year: 'numeric' 
              })}
            </div>
          </div>
          <div className="text-white/60 text-xs">Target date</div>
        </div>
      </div>

      {/* Recent Contributions */}
      {goal.contributions.length > 0 && (
        <div className="mb-4">
          <div className="text-white/60 text-xs mb-2">Recent Activity</div>
          <div className="space-y-1">
            {goal.contributions.slice(-2).map((contribution) => (
              <div key={contribution.id} className="flex justify-between text-xs">
                <span className="text-white/80">
                  {new Date(contribution.date).toLocaleDateString()}
                </span>
                <span className="text-green-400 font-medium">
                  +{formatCurrency(contribution.amount)}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Add Contribution Button */}
      {!goal.isCompleted && (
        <div className="border-t border-white/[0.06] pt-4">
          {!showContribution ? (
            <button
              onClick={() => setShowContribution(true)}
              className="w-full py-2 bg-white/[0.05] border border-white/[0.12] rounded-lg text-white/80 hover:bg-white/[0.08] hover:text-white transition-all text-sm font-medium flex items-center justify-center space-x-2"
            >
              <Plus className="w-4 h-4" />
              <span>Add Contribution</span>
            </button>
          ) : (
            <div className="space-y-3">
              <div>
                <label className="text-white text-sm font-medium mb-2 block">
                  Add Contribution
                </label>
                <div className="flex space-x-2">
                  <input
                    type="number"
                    value={contributionAmount}
                    onChange={(e) => setContributionAmount(e.target.value)}
                    placeholder="Amount"
                    className="flex-1 bg-white/[0.05] border border-white/[0.12] rounded-lg px-3 py-2 text-white text-sm focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50"
                    disabled={isAddingContribution}
                  />
                  <button
                    onClick={handleAddContribution}
                    disabled={!contributionAmount || parseFloat(contributionAmount) <= 0 || isAddingContribution}
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg text-sm font-medium disabled:opacity-50 disabled:cursor-not-allowed transition-all"
                  >
                    {isAddingContribution ? '...' : 'Add'}
                  </button>
                </div>
              </div>
              <button
                onClick={() => setShowContribution(false)}
                className="w-full py-1 text-white/60 hover:text-white/80 transition-colors text-xs"
              >
                Cancel
              </button>
            </div>
          )}
        </div>
      )}
    </UnifiedCard>
  );
});

GoalCard.displayName = 'GoalCard';

export default GoalCard;

import React, { useState, useEffect } from 'react';
import { Plus, Target, TrendingUp, Award, Calendar, DollarSign, Percent, MoreHorizontal, Clock, CheckCircle2, ArrowLeft } from 'lucide-react';
import GoalCard from './GoalCard';
import GoalCreator from './GoalCreator';
import SavingsInsights from './SavingsInsights';
import { savingsGoalsService } from '@/features/savings/api/savingsGoalsService';
import { SavingsGoal, SavingsInsight } from '@/types/savingsGoals';
import { cn } from '@/shared/lib/utils';
import { useNavigate } from 'react-router-dom';
import { UnifiedCard } from '@/shared/ui/UnifiedCard';
import { formatCurrency } from '@/shared/utils/formatters';

interface SavingsGoalsProps {
  compact?: boolean;
}

const SavingsGoals = ({ compact = false }: SavingsGoalsProps) => {
  const [goals, setGoals] = useState<SavingsGoal[]>([]);
  const [insights, setInsights] = useState<SavingsInsight[]>([]);
  const [loading, setLoading] = useState(true);
  const [showGoalCreator, setShowGoalCreator] = useState(false);
  const [activeTab, setActiveTab] = useState<'goals' | 'insights'>('goals');
  const navigate = useNavigate();

  useEffect(() => {
    loadSavingsData();
  }, []);

  const loadSavingsData = async () => {
    try {
      const [goalsData, insightsData] = await Promise.all([
        savingsGoalsService.getGoals(),
        savingsGoalsService.getSavingsInsights()
      ]);
      setGoals(goalsData);
      setInsights(insightsData);
    } catch (error) {
      console.error('Failed to load savings data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleGoalCreated = (newGoal: SavingsGoal) => {
    setGoals([...goals, newGoal]);
    setShowGoalCreator(false);
    loadSavingsData(); // Refresh insights
  };

  const handleGoalUpdate = () => {
    loadSavingsData();
  };

  const totalSaved = goals.reduce((sum, goal) => sum + goal.currentAmount, 0);
  const totalTargets = goals.reduce((sum, goal) => sum + goal.targetAmount, 0);
  const completedGoals = goals.filter(goal => goal.isCompleted).length;
  const overallProgress = totalTargets > 0 ? (totalSaved / totalTargets) * 100 : 0;

  const formatProgress = (current: number, target: number) => {
    return target > 0 ? ((current / target) * 100).toFixed(1) : '0';
  };

  const getProgressColor = (percentage: number) => {
    if (percentage >= 100) return 'text-green-400';
    if (percentage >= 75) return 'text-lime-400';
    if (percentage >= 50) return 'text-yellow-400';
    if (percentage >= 25) return 'text-orange-400';
    return 'text-red-400';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-black text-white">
        <div className="max-w-7xl mx-auto px-4 py-8 sm:px-6 lg:px-8">
          <div className="space-y-6 animate-pulse">
            <div className="h-8 bg-white/[0.05] rounded w-48"></div>
            <div className="h-32 bg-white/[0.02] rounded-xl border border-white/[0.08]"></div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              <div className="h-48 bg-white/[0.02] rounded-xl border border-white/[0.08]"></div>
              <div className="h-48 bg-white/[0.02] rounded-xl border border-white/[0.08]"></div>
              <div className="h-48 bg-white/[0.02] rounded-xl border border-white/[0.08]"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Compact mode for dashboard widget
  if (compact) {
    return (
      <UnifiedCard
        title="Savings Goals"
        icon={Target}
        iconColor="text-green-400"
        variant="default"
        size="lg"
        className="text-white"
      >
        {loading ? (
          <div className="space-y-3">
            {[...Array(2)].map((_, i) => (
              <div key={i} className="h-16 bg-white/[0.02] rounded-xl border border-white/[0.08] animate-pulse"></div>
            ))}
          </div>
        ) : goals.length === 0 ? (
          <div className="text-center py-6">
            <Target className="w-8 h-8 text-gray-400 mx-auto mb-2 opacity-50" />
            <p className="text-sm text-gray-400 mb-2">No goals yet</p>
            <button
              onClick={() => navigate('/savings')}
              className="text-xs text-blue-400 hover:text-blue-300 transition-colors"
            >
              Create your first goal
            </button>
          </div>
        ) : (
          <div className="space-y-3">
            {goals.slice(0, 3).map((goal) => {
              const progress = (goal.currentAmount / goal.targetAmount) * 100;
              const daysLeft = Math.ceil((new Date(goal.targetDate).getTime() - Date.now()) / (1000 * 60 * 60 * 24));

              return (
                <UnifiedCard
                  key={goal.id}
                  title={goal.name}
                  subtitle={goal.category}
                  metric={formatCurrency(goal.currentAmount, { currency: 'USD' })}
                  delta={{
                    value: Math.round(progress),
                    format: 'percentage',
                    label: 'complete'
                  }}
                  icon={goal.icon}
                  progress={{
                    value: goal.currentAmount,
                    max: goal.targetAmount,
                    color: progress >= 100 ? '#22c55e' : progress >= 75 ? '#84cc16' : progress >= 50 ? '#eab308' : '#ef4444',
                    showLabel: false
                  }}
                  badge={daysLeft <= 0 && !goal.isCompleted ? {
                    text: 'Overdue',
                    variant: 'error'
                  } : undefined}
                  size="sm"
                  className="hover:bg-white/[0.03] transition-all cursor-pointer"
                  onClick={() => navigate('/savings')}
                />
              );
            })}

            <div className="pt-3 border-t border-white/[0.06]">
              <button
                onClick={() => navigate('/savings')}
                className="w-full text-sm text-blue-400 hover:text-blue-300 transition-colors text-center"
              >
                View All Goals â†’
              </button>
            </div>
          </div>
        )}
      </UnifiedCard>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="max-w-7xl mx-auto px-4 py-8 sm:px-6 lg:px-8 space-y-8">
        {/* Back */}
        <button
          onClick={() => navigate('/')}
          className="flex items-center gap-2 px-3 py-2 rounded-xl text-white/80 hover:text-white transition-colors mb-4 hover:bg-white/[0.05]"
        >
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Dashboard</span>
        </button>

        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-white flex items-center space-x-3">
              <Target className="w-8 h-8 text-green-400" />
              <span>Savings Goals</span>
            </h1>
            <p className="text-gray-400 mt-2">Track your progress and build wealth systematically</p>
          </div>

          <button
            onClick={() => setShowGoalCreator(true)}
            className="px-6 py-3 bg-white/[0.05] border border-white/[0.12] rounded-xl font-medium hover:bg-white/[0.08] transition-all flex items-center space-x-2 text-white backdrop-blur-md"
          >
            <Plus className="w-5 h-5" />
            <span>New Goal</span>
          </button>
        </div>

        {/* Overview Stats using UnifiedCard */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <UnifiedCard
            title="Total Saved"
            subtitle="Across all goals"
            metric={formatCurrency(totalSaved, { currency: 'USD' })}
            delta={{
              value: parseFloat(formatProgress(totalSaved, totalTargets)),
              format: 'percentage',
              label: 'of target'
            }}
            icon={DollarSign}
            iconColor="text-green-400"
            variant="default"
            size="lg"
          />

          <UnifiedCard
            title="Total Targets"
            subtitle="Goal amounts"
            metric={formatCurrency(totalTargets, { currency: 'USD' })}
            delta={{
              value: goals.length,
              format: 'number',
              label: 'active goals'
            }}
            icon={Target}
            iconColor="text-blue-400"
            variant="default"
            size="lg"
          />

          <UnifiedCard
            title="Completed"
            subtitle="Goals achieved"
            metric={completedGoals.toString()}
            delta={{
              value: Math.round(goals.length > 0 ? (completedGoals / goals.length) * 100 : 0),
              format: 'percentage',
              label: 'success rate'
            }}
            icon={Award}
            iconColor="text-purple-400"
            variant="default"
            size="lg"
          />

          <UnifiedCard
            title="Overall Progress"
            subtitle="Average completion"
            metric={`${Math.round(overallProgress)}%`}
            progress={{
              value: overallProgress,
              max: 100,
              color: overallProgress >= 80 ? '#22c55e' : overallProgress >= 60 ? '#84cc16' : overallProgress >= 40 ? '#eab308' : overallProgress >= 20 ? '#f97316' : '#ef4444',
              showLabel: false
            }}
            icon={Percent}
            iconColor="text-orange-400"
            variant="default"
            size="lg"
          />
        </div>

        {/* Tab Navigation */}
        <div className="flex space-x-1 bg-white/[0.05] p-1 rounded-xl">
          {[
            { id: 'goals', label: 'My Goals', icon: Target },
