        </div>
        {/* Mobile shipping status inline second row*/}
        {transaction.trackingNumber && (
          <div className="flex lg:hidden items-center gap-1 text-xs text-white/50 mt-0.5">
            {prettyShipStatus(transaction.shippingStatus)}
          </div>
