/**
 * LazyChartWrapper - Optimized chart loading with IntersectionObserver
 * Reduces vendor-charts bundle by loading charts only when visible
 * Target: vendor-charts â‰¤ 300KB
 */

import React, { 
  useState, 
  useEffect, 
  useRef, 
  lazy, 
  Suspense, 
  ReactNode 
} from 'react';
import { cn } from '@/shared/lib/utils';

// Chart skeleton with theme-aware styling
const ChartSkeleton: React.FC<{
  height?: number;
  className?: string;
  showTitle?: boolean;
  title?: string;
}> = ({ height = 180, className, showTitle = false, title }) => (
  <div 
    className={cn(
      "bg-white/[0.02] rounded-lg border border-white/[0.08] animate-pulse",
      "flex flex-col justify-center items-center p-6",
      className
    )}
    style={{ height }}
  >
    {showTitle && title && (
      <div className="w-full mb-4">
        <div className="h-5 bg-white/[0.05] rounded w-1/3 mb-2"></div>
        <div className="h-3 bg-white/[0.03] rounded w-1/2"></div>
      </div>
    )}

    <div className="flex-1 w-full flex items-center justify-center">
      <div className="flex items-center space-x-2 text-white/60">
        <div className="w-6 h-6 border-2 border-blue-500/30 border-t-blue-500 rounded-full animate-spin"></div>
        <span className="text-sm">Loading chart...</span>
      </div>
    </div>

    {/* Chart-like skeleton elements */}
    <div className="w-full flex items-end justify-center space-x-1 mt-4">
      {[...Array(8)].map((_, i) => (
        <div
          key={i}
          className="bg-white/[0.05] rounded-t-sm"
          style={{
            width: '12px',
            height: `${Math.random() * 30 + 10}px`,
            animationDelay: `${i * 100}ms`,
          }}
        />
      ))}
    </div>
  </div>
);

// Intersection Observer hook for lazy loading
const useIntersectionObserver = (
  ref: React.RefObject<Element>,
  options: IntersectionObserverInit = {}
) => {
  const [isIntersecting, setIsIntersecting] = useState(false);
  const [hasIntersected, setHasIntersected] = useState(false);

  useEffect(() => {
    const element = ref.current;
    if (!element) return;

    const observer = new IntersectionObserver(([entry]) => {
      setIsIntersecting(entry.isIntersecting);
      if (entry.isIntersecting && !hasIntersected) {
        setHasIntersected(true);
      }
    }, {
      rootMargin: '50px', // Start loading 50px before visible
      threshold: 0.1,
      ...options
    });

    observer.observe(element);

    return () => {
      observer.unobserve(element);
    };
  }, [ref, hasIntersected, options]);

  return { isIntersecting, hasIntersected };
};

// Main lazy chart wrapper component
interface LazyChartWrapperProps {
  children: ReactNode;
  height?: number;
  title?: string;
  className?: string;
  preload?: boolean; // Force immediate loading
  threshold?: number; // Intersection threshold
}

export const LazyChartWrapper: React.FC<LazyChartWrapperProps> = ({
  children,
  height = 180,
  title,
  className,
  preload = false,
  threshold = 0.1
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const { hasIntersected } = useIntersectionObserver(containerRef, { threshold });

  const shouldLoad = preload || hasIntersected;

  return (
    <div 
      ref={containerRef}
      className={cn("w-full", className)}
    >
      {shouldLoad ? (
        <Suspense 
          fallback={
            <ChartSkeleton 
              height={height} 
              showTitle={!!title}
              title={title}
              className={className}
            />
          }
        >
          {children}
        </Suspense>
      ) : (
        <ChartSkeleton 
          height={height} 
          showTitle={!!title}
          title={title}
          className={className}
        />
      )}
    </div>
  );
};

// Performance utilities
export const chartPerformance = {
  // Preload charts for better UX on fast connections
  preloadCharts: (): void => {
    if ('connection' in navigator) {
      const connection = (navigator as any).connection;
      if (connection?.effectiveType === '4g' && !connection?.saveData) {
        // Preload on fast connections only
        import('recharts');
      }
    }
  },

  // Get optimal loading strategy based on device capabilities
  getLoadingStrategy: (): 'immediate' | 'intersection' | 'defer' => {
    // Check device capabilities
    if ('deviceMemory' in navigator) {
      const memory = (navigator as any).deviceMemory;
      if (memory <= 2) return 'defer';
      if (memory >= 8) return 'immediate';
    }

    return 'intersection';
  },

  // Monitor chart loading performance
  trackChartLoad: (chartType: string, startTime: number): void => {
    const loadTime = performance.now() - startTime;
    console.log(`[Chart Performance] ${chartType} loaded in ${loadTime.toFixed(2)}ms`);

    // Send to analytics in production
    if (import.meta.env.PROD && 'sendBeacon' in navigator) {
      navigator.sendBeacon('/api/performance/chart', JSON.stringify({
        chartType,
        loadTime,
        timestamp: Date.now()
      }));
    }
  }
};

export default LazyChartWrapper;

/**
 * LineChart - Apple-style line chart component
 * Extends GraphBase foundation with line-specific features
 * Based on Apple Human Interface Guidelines 2025
 */

import React, { 
  memo, 
  useMemo, 
  forwardRef, 
  useCallback,
  useImperativeHandle,
  useRef
} from 'react';
import { cn } from '@/shared/lib/utils';
import { GraphBase } from './GraphBase';
import { 
  appleGraphTokens,
  getGraphColor,
  getTextColor,
  getChartAnimationPreset,
  shouldReduceMotion,
  getOptimalAnimationDuration 
} from '@/theme/graph-tokens';
import {
  GraphBaseProps,
  ChartDataPoint,
  ChartSeries,
  ChartRef,
  TimeRangeOption
} from './types';

// Line-specific configuration
export interface LineChartConfig {
  // Line styling
  smoothLines?: boolean;
  strokeWidth?: 'thin' | 'medium' | 'thick';
  lineCap?: 'round' | 'square' | 'butt';
  lineJoin?: 'round' | 'bevel' | 'miter';

  // Point styling
  showDots?: boolean;
  dotSize?: 'small' | 'medium' | 'large';
  activeDotSize?: 'small' | 'medium' | 'large';

  // Gradient fills
  gradientFill?: boolean;
  gradientOpacity?: number;

  // Interaction
  hoverEffects?: boolean;
  crosshair?: boolean;

  // Financial-specific
  currencyFormat?: boolean;
  percentageFormat?: boolean;
  trendIndicators?: boolean;
}

// Extended props for LineChart
export interface LineChartProps extends Omit<GraphBaseProps, 'type'> {
  lineConfig?: LineChartConfig;

  // Financial data helpers
  financialType?: 'currency' | 'percentage' | 'number';
  trendAnalysis?: boolean;

  // Multiple series shortcuts
  multiSeries?: boolean;
  seriesColors?: string[];

  // Apple-specific enhancements
  appleAnimation?: boolean;
  precisionReduce?: boolean;
}

// Default line chart configuration
const DEFAULT_LINE_CONFIG: LineChartConfig = {
  smoothLines: true,
  strokeWidth: 'medium',
  lineCap: 'round',
  lineJoin: 'round',
  showDots: false,
  dotSize: 'small',
  activeDotSize: 'medium',
  gradientFill: true,
  gradientOpacity: 0.1,
  hoverEffects: true,
  crosshair: false,
  currencyFormat: false,
  percentageFormat: false,
  trendIndicators: false,
};

// Stroke width mapping
const STROKE_WIDTH_MAP = {
  thin: 1.5,
  medium: 2,
  thick: 3,
} as const;

// Dot size mapping
const DOT_SIZE_MAP = {
  small: 3,
  medium: 4,
  large: 6,
} as const;

// Custom tooltip for financial data
const FinancialTooltip: React.FC<{
  active?: boolean;
  payload?: any[];
  label?: string;
  financialType?: 'currency' | 'percentage' | 'number';
}> = memo(({ active, payload, label, financialType = 'number' }) => {
  if (!active || !payload || !payload.length) return null;

  const formatValue = (value: number) => {
    switch (financialType) {
      case 'currency':
        return new Intl.NumberFormat('en-US', {
          style: 'currency',
          currency: 'USD',
          minimumFractionDigits: 0,
          maximumFractionDigits: 0,
        }).format(value);
      case 'percentage':
        return `${value.toFixed(1)}%`;
      default:
        return value.toLocaleString();
    }
  };

  const formatDate = (dateStr: string) => {
    try {
      const date = new Date(dateStr);
      return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric'
      });
    } catch {
      return dateStr;
    }
  };

  return (
    <div className={cn(
      "bg-black/90 backdrop-blur-md rounded-xl px-4 py-3 shadow-2xl",
      "border border-white/20 max-w-xs"
    )}>
      <p className="text-white/60 text-xs font-medium mb-2">
        {formatDate(label || '')}
      </p>
      <div className="space-y-1">
        {payload.map((entry, index) => (
          <div key={index} className="flex items-center justify-between space-x-3">
            <div className="flex items-center space-x-2">
              <div
                className="w-2 h-2 rounded-full"
                style={{ backgroundColor: entry.color }}
              />
              <span className="text-white/80 text-xs">
                {entry.name}
              </span>
            </div>
            <span className="text-white font-medium text-sm">
              {formatValue(entry.value)}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
});

FinancialTooltip.displayName = 'FinancialTooltip';

// Main LineChart component
export const LineChart = forwardRef<ChartRef, LineChartProps>(({
  // Line-specific props
  lineConfig = {},
  financialType = 'number',
  trendAnalysis = false,
  multiSeries = false,
  seriesColors,
  appleAnimation = true,
  precisionReduce = true,

  // GraphBase props
  data,
  series,
  title,
  subtitle,
  headerActions,

  // Styling
  className,
  style,

  // Chart configuration
  animation,
  tooltip,

  // Event handlers
  onDataPointClick,
  onDataPointHover,

  ...rest
}, ref) => {
  const internalRef = useRef<HTMLDivElement>(null);

  // Forward ref to internal element
  useImperativeHandle(ref, () => internalRef.current as HTMLDivElement);

  // Merge line configuration with defaults
  const lineConfigMerged = useMemo(() => ({
    ...DEFAULT_LINE_CONFIG,
    ...lineConfig,
  }), [lineConfig]);

  // Generate optimized series for line charts with Apple-standard animations
  const optimizedSeries = useMemo((): ChartSeries[] => {
    const hoverPreset = getChartAnimationPreset('hover');

    if (series) {
      return series.map((serie, index) => ({
        ...serie,
        strokeWidth: STROKE_WIDTH_MAP[lineConfigMerged.strokeWidth || 'medium'],
        connectNulls: true,
        color: serie.color || (seriesColors?.[index]) || getGraphColor(
          ['income', 'spending', 'savings', 'investments', 'debt'][index] as any || 'neutral',
          'dark'
        ),
        // Apple-style hover animations
        activeDot: {
          r: DOT_SIZE_MAP[lineConfigMerged.activeDotSize || 'medium'],
          fill: serie.color || getGraphColor(
            ['income', 'spending', 'savings', 'investments', 'debt'][index] as any || 'neutral',
            'dark'
          ),
          strokeWidth: 2,
          stroke: '#ffffff',
          style: {
            filter: 'drop-shadow(0px 2px 4px rgba(0,0,0,0.2))',
            transition: `all ${hoverPreset.duration}ms ${hoverPreset.easing}`,
          }
        },
        dot: lineConfigMerged.showDots ? {
          r: DOT_SIZE_MAP[lineConfigMerged.dotSize || 'small'],
          fill: serie.color,
          strokeWidth: 1,
          stroke: '#ffffff',
        } : false,
      }));
    }

    // Auto-generate series from data
    if (data.length === 0) return [];

    const firstPoint = data[0];
    const numericKeys = Object.keys(firstPoint).filter(key => 
      key !== 'date' && 
      key !== 'label' && 
      typeof firstPoint[key] === 'number'
    );

    return numericKeys.slice(0, multiSeries ? 6 : 1).map((key, index) => {
      const color = seriesColors?.[index] || getGraphColor(
        ['income', 'spending', 'savings', 'investments', 'debt'][index] as any || 'neutral',
        'dark'
      );

      return {
        dataKey: key,
        label: key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1'),
        color,
        strokeWidth: STROKE_WIDTH_MAP[lineConfigMerged.strokeWidth || 'medium'],
        connectNulls: true,
        // Apple-style hover animations
        activeDot: {
          r: DOT_SIZE_MAP[lineConfigMerged.activeDotSize || 'medium'],
          fill: color,
          strokeWidth: 2,
          stroke: '#ffffff',
          style: {
            filter: 'drop-shadow(0px 2px 4px rgba(0,0,0,0.2))',
            transition: `all ${hoverPreset.duration}ms ${hoverPreset.easing}`,
          }
        },
        dot: lineConfigMerged.showDots ? {
          r: DOT_SIZE_MAP[lineConfigMerged.dotSize || 'small'],
          fill: color,
          strokeWidth: 1,
          stroke: '#ffffff',
        } : false,
      };
    });
  }, [data, series, lineConfigMerged.strokeWidth, lineConfigMerged.activeDotSize, lineConfigMerged.dotSize, lineConfigMerged.showDots, multiSeries, seriesColors]);

  // Optimize data for performance if needed
  const optimizedData = useMemo(() => {
    if (!precisionReduce || data.length <= 100) return data;

    // Simple data reduction for large datasets
    const step = Math.ceil(data.length / 100);
    return data.filter((_, index) => index % step === 0);
  }, [data, precisionReduce]);

  // Enhanced animation configuration with Apple standards
  const animationConfig = useMemo(() => {
    const lineAnimationPreset = getChartAnimationPreset('line');
    const reducedMotion = shouldReduceMotion();

    return {
      enable: appleAnimation && !reducedMotion,
      duration: lineAnimationPreset.duration,
      easing: lineAnimationPreset.easing,
      delay: lineAnimationPreset.delay,
      // Override with any custom animation settings
      ...animation,
      // Ensure reduced motion is respected
      ...(reducedMotion && { 
        enable: false, 
        duration: 0 
      }),
    };
  }, [appleAnimation, animation]);

  // Custom tooltip configuration
  const tooltipConfig = useMemo(() => ({
    show: true,
    ...tooltip,
  }), [tooltip]);

  // Custom tooltip component
  const customTooltip = useCallback((props: any) => (
    <FinancialTooltip {...props} financialType={financialType} />
  ), [financialType]);

  // Enhanced Y-axis formatter
  const yAxisFormatter = useCallback((value: number) => {
    if (financialType === 'currency') {
      // Shortened currency format for axis
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(1)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(0)}K`;
      }
      return `$${value.toFixed(0)}`;
    }

    if (financialType === 'percentage') {
      return `${value}%`;
    }

    return value.toLocaleString();
  }, [financialType]);

  // Generate trend indicators if enabled
  const trendIndicator = useMemo(() => {
    if (!trendAnalysis || !data.length || !optimizedSeries.length) return null;

    const firstValue = data[0]?.[optimizedSeries[0].dataKey];
    const lastValue = data[data.length - 1]?.[optimizedSeries[0].dataKey];

    if (typeof firstValue !== 'number' || typeof lastValue !== 'number') return null;

    const change = lastValue - firstValue;
    const changePercent = (change / firstValue) * 100;
    const isPositive = change > 0;

    return (
      <div className="flex items-center space-x-2 text-sm">
        <div className={cn(
          "flex items-center space-x-1 px-2 py-1 rounded-full",
          isPositive ? "bg-green-500/20 text-green-400" : "bg-red-500/20 text-red-400"
        )}>
          <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 12 12">
            {isPositive ? (
              <path d="M3.75 6.75L6 4.5L8.25 6.75" stroke="currentColor" strokeWidth="1.5" fill="none" strokeLinecap="round" strokeLinejoin="round" />
            ) : (
              <path d="M8.25 5.25L6 7.5L3.75 5.25" stroke="currentColor" strokeWidth="1.5" fill="none" strokeLinecap="round" strokeLinejoin="round" />
            )}
          </svg>
          <span className="font-medium">
            {changePercent > 0 ? '+' : ''}{changePercent.toFixed(1)}%
          </span>
        </div>
      </div>
    );
  }, [trendAnalysis, data, optimizedSeries]);

  return (
    <GraphBase
      ref={internalRef}
      // Core props
      data={optimizedData}
      type="line"
      series={optimizedSeries}

      // Header
      title={title}
      subtitle={subtitle}
      headerActions={
        <>
          {headerActions}
          {trendIndicator}
        </>
      }

      // Chart configuration
      animation={animationConfig}
      tooltip={tooltipConfig}
      customTooltip={customTooltip}

      // Y-axis formatting
      yAxis={{
        show: true,
        tickFormatter: yAxisFormatter,
      }}

      // Grid configuration optimized for line charts
      grid={{
        show: true,
        horizontal: true,
        vertical: false,
        strokeDasharray: "2 2",
        opacity: 0.3,
      }}

      // Styling
      className={cn("line-chart", className)}
      style={style}

      // Event handlers
      onDataPointClick={onDataPointClick}
      onDataPointHover={onDataPointHover}

      {...rest}
    />
  );
});

LineChart.displayName = 'LineChart';

// Export memoized version
export default memo(LineChart);

/**
 * StackedBarChart - Apple-style stacked bar chart component
 * Extends GraphBase foundation with specialized stacked bar functionality
 * Perfect for spending breakdowns, budget comparisons, and portfolio allocations
 */

import React, { memo, useMemo, useCallback, forwardRef, useImperativeHandle, useRef } from 'react';
import { 
  ResponsiveContainer,
  BarChart,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  Bar,
  Cell
} from 'recharts';
import { cn } from '@/shared/lib/utils';
import { 
  appleGraphTokens,
  getGraphColor,
  getTextColor,
  getBackgroundColor,
  generateGraphCSSProperties,
  getChartAnimationPreset,
  shouldReduceMotion,
  getOptimalAnimationDuration 
} from '@/theme/graph-tokens';
import { GraphBase } from './GraphBase';
import { 
  ChartDataPoint, 
  ChartSeries, 
  ChartRef,
  TimeRangeOption,
  LoadingState,
  AccessibilityConfig
} from './types';

// Enhanced data point interface for stacked bars
export interface StackedBarDataPoint extends ChartDataPoint {
  total?: number;
  categories?: { [key: string]: number };
  percentages?: { [key: string]: number };
}

// Stacked bar specific configuration
export interface StackedBarConfig {
  // Display modes
  displayMode?: 'absolute' | 'percentage' | 'both';
  showTotal?: boolean;

  // Bar styling
  barRadius?: number;
  barGap?: number;
  categoryGap?: number;
  maxBarHeight?: number;

  // Color and styling
  colorScheme?: 'financial' | 'categorical' | 'custom';
  gradientFill?: boolean;

  // Category management
  maxCategories?: number;
  groupSmallCategories?: boolean;
  smallCategoryThreshold?: number;

  // Interactions
  hoverEffects?: boolean;
  clickableSegments?: boolean;
  animateOnLoad?: boolean;
}

// Financial category mappings for automatic color assignment
const FINANCIAL_CATEGORY_COLORS = {
  // Spending categories
  food: '#FF453A',           // Apple red
  housing: '#FF9F0A',        // Apple orange
  transportation: '#FFCC00', // Apple yellow
  entertainment: '#AF52DE',  // Apple purple
  healthcare: '#FF375F',     // Apple pink
  shopping: '#5AC8FA',       // Apple teal
  utilities: '#32D74B',      // Apple green
  debt_payments: '#FF3B30',  // Apple red light
  savings: '#007AFF',        // Apple blue
  other: '#8E8E93',          // Apple gray

  // Investment categories
  stocks: '#007AFF',         // Apple blue
  bonds: '#32D74B',          // Apple green
  cash: '#FFCC00',           // Apple yellow
  crypto: '#AF52DE',         // Apple purple
  real_estate: '#FF9F0A',    // Apple orange
  commodities: '#5AC8FA',    // Apple teal

  // Income categories
  salary: '#32D74B',         // Apple green
  freelance: '#007AFF',      // Apple blue
  investments: '#AF52DE',    // Apple purple
  business: '#FF9F0A',       // Apple orange
  rental: '#5AC8FA',         // Apple teal
};

// Props interface for StackedBarChart
export interface StackedBarChartProps {
  // Core data
  data: StackedBarDataPoint[];
  series?: ChartSeries[];

  // Header and metadata
  title?: string;
  subtitle?: string;
  headerActions?: React.ReactNode;

  // Stacked bar configuration
  stackedBarConfig?: StackedBarConfig;

  // Financial formatting
  financialType?: 'currency' | 'percentage' | 'number';
  currencyCode?: string;

  // Chart dimensions and styling
  dimensions?: {
    height?: number;
    minHeight?: number;
    maxHeight?: number;
    responsive?: boolean;
  };
  className?: string;
  style?: React.CSSProperties;

  // Time controls
  timeRange?: TimeRangeOption;
  timeControls?: {
    show: boolean;
    options: TimeRangeOption[];
    defaultRange: TimeRangeOption;
  };
  onTimeRangeChange?: (range: TimeRangeOption) => void;

  // State management
  loading?: boolean;
  loadingState?: LoadingState;
  error?: string | Error;

  // Accessibility
  accessibility?: AccessibilityConfig;

  // Event handlers
  onBarClick?: (data: StackedBarDataPoint, categoryKey: string, value: number) => void;
  onBarHover?: (data: StackedBarDataPoint | null, categoryKey: string | null) => void;
  onChartReady?: () => void;

  // Advanced
  customTooltip?: React.ComponentType<any>;
  customLegend?: React.ComponentType<any>;
  children?: React.ReactNode;
}

// Enhanced tooltip for stacked bars
const StackedBarTooltip: React.FC<{
  active?: boolean;
  payload?: any[];
  label?: string;
  financialType?: 'currency' | 'percentage' | 'number';
  displayMode?: 'absolute' | 'percentage' | 'both';
}> = memo(({ active, payload, label, financialType = 'currency', displayMode = 'absolute' }) => {
  if (!active || !payload || !payload.length) return null;

  const formatValue = useCallback((value: number, type: 'currency' | 'percentage' | 'number') => {
    switch (type) {
      case 'currency':
        return new Intl.NumberFormat('en-US', {
          style: 'currency',
          currency: 'USD',
          minimumFractionDigits: 0,
          maximumFractionDigits: 2
        }).format(value);
      case 'percentage':
        return `${value.toFixed(1)}%`;
      case 'number':
        return value.toLocaleString();
      default:
        return value.toString();
    }
  }, []);

  // Calculate total for percentage display
  const total = payload.reduce((sum, entry) => sum + (entry.value || 0), 0);

  return (
    <div className="bg-black/90 backdrop-blur-sm border border-white/10 rounded-lg p-3 shadow-lg">
      <p className="text-white font-medium mb-2">{label}</p>
      <div className="space-y-1">
        {payload.map((entry, index) => (
          <div key={index} className="flex items-center justify-between gap-4">
            <div className="flex items-center gap-2">
              <div 
                className="w-3 h-3 rounded-sm"
                style={{ backgroundColor: entry.color }}
              />
              <span className="text-white/80 text-sm capitalize">
                {entry.dataKey?.replace(/_/g, ' ')}
              </span>
            </div>
            <div className="text-right">
              <span className="text-white font-medium">
                {formatValue(entry.value, financialType)}
              </span>
              {displayMode === 'both' && total > 0 && (
                <span className="text-white/60 text-xs ml-2">
                  ({((entry.value / total) * 100).toFixed(1)}%)
                </span>
              )}
            </div>
          </div>
        ))}
        {displayMode !== 'percentage' && (
          <div className="border-t border-white/10 pt-2 mt-2">
            <div className="flex justify-between">
              <span className="text-white/60 text-sm">Total:</span>
              <span className="text-white font-medium">
                {formatValue(total, financialType)}
              </span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
});

StackedBarTooltip.displayName = 'StackedBarTooltip';

// Main StackedBarChart component
export const StackedBarChart = forwardRef<ChartRef, StackedBarChartProps>(({
  data,
  series,
  title,
  subtitle,
  headerActions,
  stackedBarConfig = {},
  financialType = 'currency',
  currencyCode = 'USD',
  dimensions = { height: 300, responsive: true },
  className,
  style,
  timeRange,
  timeControls,
  onTimeRangeChange,
  loading = false,
  loadingState = 'idle',
  error,
  accessibility = { keyboardNavigation: true },
  onBarClick,
  onBarHover,
  onChartReady,
  customTooltip,
  customLegend,
  children,
  ...rest
}, ref) => {
  const containerRef = useRef<HTMLDivElement>(null);

  // Expose ref imperatively
  useImperativeHandle(ref, () => containerRef.current as HTMLDivElement);

  // Destructure stacked bar config with defaults
  const {
    displayMode = 'absolute',
    showTotal = true,
    barRadius = 8,
    barGap = 4,
    categoryGap = 16,
    colorScheme = 'financial',
    gradientFill = false,
    maxCategories = 8,
    groupSmallCategories = true,
    smallCategoryThreshold = 0.05,
    hoverEffects = true,
    clickableSegments = true,
    animateOnLoad = true
  } = stackedBarConfig;

  // Auto-generate series if not provided
  const computedSeries = useMemo((): ChartSeries[] => {
    if (series) return series;

    if (data.length === 0) return [];

    // Collect all category keys from data
    const categoryKeys = new Set<string>();
    data.forEach(point => {
      Object.keys(point).forEach(key => {
        if (key !== 'date' && key !== 'label' && key !== 'total' && key !== 'categories' && key !== 'percentages') {
          if (typeof point[key] === 'number') {
            categoryKeys.add(key);
          }
        }
      });
    });

    const categories = Array.from(categoryKeys);

    // Sort categories by total value (largest first)
    if (groupSmallCategories) {
      const categoryTotals = categories.map(key => ({
        key,
        total: data.reduce((sum, point) => sum + (point[key] as number || 0), 0)
      }));

      categoryTotals.sort((a, b) => b.total - a.total);

      // Group small categories if needed
      if (categoryTotals.length > maxCategories) {
        const mainCategories = categoryTotals.slice(0, maxCategories - 1);
        const otherCategories = categoryTotals.slice(maxCategories - 1);

        return [
          ...mainCategories.map(({ key }, index) => ({
            dataKey: key,
            label: key.charAt(0).toUpperCase() + key.slice(1).replace(/_/g, ' '),
            color: FINANCIAL_CATEGORY_COLORS[key as keyof typeof FINANCIAL_CATEGORY_COLORS] || 
                   getGraphColor(['income', 'spending', 'savings', 'investments', 'debt'][index % 5] as any, 'dark')
          })),
          {
            dataKey: 'other',
            label: 'Other',
            color: '#8E8E93' // Apple gray
          }
        ];
      }

      return categoryTotals.map(({ key }, index) => ({
        dataKey: key,
        label: key.charAt(0).toUpperCase() + key.slice(1).replace(/_/g, ' '),
        color: FINANCIAL_CATEGORY_COLORS[key as keyof typeof FINANCIAL_CATEGORY_COLORS] || 
               getGraphColor(['income', 'spending', 'savings', 'investments', 'debt'][index % 5] as any, 'dark')
      }));
    }

    return categories.slice(0, maxCategories).map((key, index) => ({
      dataKey: key,
      label: key.charAt(0).toUpperCase() + key.slice(1).replace(/_/g, ' '),
      color: FINANCIAL_CATEGORY_COLORS[key as keyof typeof FINANCIAL_CATEGORY_COLORS] || 
             getGraphColor(['income', 'spending', 'savings', 'investments', 'debt'][index % 5] as any, 'dark')
    }));
  }, [data, series, maxCategories, groupSmallCategories]);

  // Process data for percentage mode
  const processedData = useMemo(() => {
    if (displayMode === 'absolute') return data;

    return data.map(point => {
      const total = computedSeries.reduce((sum, serie) => {
        return sum + (point[serie.dataKey] as number || 0);
      }, 0);

      const processed = { ...point };

      if (displayMode === 'percentage' && total > 0) {
        computedSeries.forEach(serie => {
          const value = point[serie.dataKey] as number || 0;
          processed[serie.dataKey] = (value / total) * 100;
        });
      }

      processed.total = total;
      return processed;
    });
  }, [data, computedSeries, displayMode]);

  // Format value for display
  const formatValue = useCallback((value: number) => {
    if (displayMode === 'percentage') {
      return `${value.toFixed(1)}%`;
    }

    switch (financialType) {
      case 'currency':
        return new Intl.NumberFormat('en-US', {
          style: 'currency',
          currency: currencyCode,
          minimumFractionDigits: 0,
          maximumFractionDigits: 2
        }).format(value);
      case 'percentage':
        return `${value.toFixed(1)}%`;
      case 'number':
        return value.toLocaleString();
      default:
        return value.toString();
    }
  }, [displayMode, financialType, currencyCode]);

  // Handle bar click events
  const handleBarClick = useCallback((data: any, index: number) => {
    if (!clickableSegments || !onBarClick) return;

    // Find which segment was clicked
    const point = processedData[index];
    if (point) {
      // For now, we'll just trigger with the first series key
      // In a real implementation, you'd determine which segment was clicked
      const firstSeries = computedSeries[0];
      if (firstSeries) {
        onBarClick(point, firstSeries.dataKey, point[firstSeries.dataKey] as number);
      }
    }
  }, [clickableSegments, onBarClick, processedData, computedSeries]);

  // Handle bar hover events
  const handleBarHover = useCallback((state: any) => {
    if (!hoverEffects || !onBarHover) return;

    if (state.isTooltipActive && state.activePayload && state.activePayload.length > 0) {
      const point = state.activePayload[0].payload as StackedBarDataPoint;
      const categoryKey = state.activePayload[0].dataKey;
      onBarHover(point, categoryKey);
    } else {
      onBarHover(null, null);
    }
  }, [hoverEffects, onBarHover]);

  // Custom legend component
  const renderLegend = useCallback(() => {
    if (customLegend) {
      return React.createElement(customLegend);
    }

    return (
      <div className="flex flex-wrap justify-center gap-4 mt-4">
        {computedSeries.map((serie) => (
          <div key={serie.dataKey} className="flex items-center gap-2">
            <div 
              className="w-3 h-3 rounded-sm"
              style={{ backgroundColor: serie.color }}
            />
            <span className="text-white/80 text-sm">{serie.label}</span>
          </div>
        ))}
      </div>
    );
  }, [computedSeries, customLegend]);

  // Generate CSS properties for theme support
  const cssProperties = useMemo(() => 
    generateGraphCSSProperties('dark'), []
  );

  // Use GraphBase for core functionality
  return (
    <GraphBase
      ref={containerRef}
      data={processedData}
      type="stackedBar"
      series={computedSeries}
      title={title}
      subtitle={subtitle}
      headerActions={headerActions}
      timeRange={timeRange}
      timeControls={timeControls}
      onTimeRangeChange={onTimeRangeChange}
      dimensions={dimensions}
      className={cn("stacked-bar-chart", className)}
      style={{ ...cssProperties, ...style }}
      loading={loading}
      loadingState={loadingState}
      error={error}
      accessibility={accessibility}
      onChartReady={onChartReady}
      customTooltip={customTooltip || StackedBarTooltip}
      customLegend={renderLegend}
      animation={useMemo(() => {
        const barAnimationPreset = getChartAnimationPreset('stacked');
        const reducedMotion = shouldReduceMotion();

        return {
          enable: animateOnLoad && !reducedMotion,
          duration: barAnimationPreset.duration, // 400ms for staggered bars
          easing: barAnimationPreset.easing,     // iOS ease-out
          delay: barAnimationPreset.delay,       // No delay for bars
          // Ensure reduced motion is respected
          ...(reducedMotion && { 
            enable: false, 
            duration: 0,
            delay: 0 
          }),
        };
      }, [animateOnLoad])}
      {...rest}
    >
      {children}
    </GraphBase>
  );
});

StackedBarChart.displayName = 'StackedBarChart';

// Export memoized version
export default memo(StackedBarChart);

